	public Boolean tieneElementosSeleccionados() throws Exception {
		Boolean tiene=false;
		
		List<DetalleCliente> detalleclientesLocal=null;
		
		try	{			
			//ARCHITECTURE
			if(Constantes.ISUSAEJBLOGICLAYER) {
				detalleclientesLocal=this.detalleclienteLogic.getDetalleClientes();
			} else if(Constantes.ISUSAEJBREMOTE || Constantes.ISUSAEJBHOME) {
				detalleclientesLocal=this.detalleclientes;
			}
			//ARCHITECTURE
		
			for(DetalleCliente detalleclienteLocal:detalleclientesLocal) {
				if(this.permiteMantenimiento(detalleclienteLocal) && detalleclienteLocal.getIsSelected()) {
					tiene=true;
					break;
				}
			}
		} catch(Exception e) {
			throw e;
		}
		
		return tiene;
	}			
	
