	public void nuevoPreparar(Boolean esNuevoGuardarCambios) throws Exception {
		this.iIdNuevoDetalleCliente--;	
		
		
		this.detalleclienteAux=new DetalleCliente();
		
		this.detalleclienteAux.setId(this.iIdNuevoDetalleCliente);
		this.detalleclienteAux.setIsChanged(true);
		
		//ARCHITECTURE
		if(Constantes.ISUSAEJBLOGICLAYER) {
			this.detalleclienteLogic.getDetalleClientes().add(this.detalleclienteAux);
		} else if(Constantes.ISUSAEJBREMOTE || Constantes.ISUSAEJBHOME) {
			this.detalleclientes.add(this.detalleclienteAux);
		}
		//ARCHITECTURE
		
		this.detallecliente=this.detalleclienteAux;
		
		if(DetalleClienteJInternalFrame.ISBINDING_MANUAL_TABLA) {
			this.setVariablesObjetoActualToFormularioDetalleCliente(this.detallecliente);
			this.setVariablesObjetoActualToFormularioForeignKeyDetalleCliente(this.detallecliente);
		}
				
		//this.setDefaultControlesDetalleCliente();
		
		this.inicializarInvalidValues();
				
		//SELECCIONA ITEM DEFECTO-->SET O SELECTED INDEX
		this.setItemDefectoCombosForeignKeyDetalleCliente();
		
		
		//INICIALIZA VARIABLES COMBOS GLOBALES A FORMULARIO(ParametroGeneralUsuario)
		this.setVariablesGlobalesCombosForeignKeyDetalleCliente();
		
		//INICIALIZA VARIABLES COMBOS GLOBALES AUXILIARES A FORMULARIO(Anio,Mes)
		//this.setVariablesGlobalesAuxiliaresCombosForeignKeyDetalleCliente();
		
		//SI TIENE FOREIGN KEY CON CAMPO esDefecto=true, SE ACTUALIZA A OBJETO ACTUAL
		this.setVariablesForeignKeyObjetoBeanDefectoActualToObjetoActualDetalleCliente(this.detalleclienteBean,this.detallecliente,false,false);
		
		//ACTUALIZA VALORES PARA EL OBJETO ACTUAL ANTES DE ENVIARLO A ACTUALIZAR
		this.setVariablesFormularioToObjetoActualForeignKeysDetalleCliente(this.detallecliente);
				
		ArrayList<Classe> classes=new ArrayList<Classe>();
		
		if(this.jInternalFrameParent!=null) {
			this.jInternalFrameParent.actualizarObjetoPadreFk(DetalleClienteConstantesFunciones.CLASSNAME);
		}	
		
		
		if(this.detalleclienteSessionBean.getConGuardarRelaciones()) {
			classes=DetalleClienteConstantesFunciones.getClassesRelationshipsOfDetalleCliente(new ArrayList<Classe>(),DeepLoadType.NONE);
		}
		
		this.classesActual=new ArrayList<Classe>();
		this.classesActual.addAll(classes);
				
		//ARCHITECTURE
		if(Constantes.ISUSAEJBLOGICLAYER) {						
			this.detalleclienteReturnGeneral=detalleclienteLogic.procesarEventosDetalleClientesWithConnection(this.parametroGeneralUsuario,this.moduloActual,this.opcionActual,this.usuarioActual,EventoGlobalTipo.FORM_RECARGAR,ControlTipo.FORM,EventoTipo.LOAD,EventoSubTipo.NEW,"FORM",this.detalleclienteLogic.getDetalleClientes(),this.detallecliente,this.detalleclienteParameterGeneral,this.isEsNuevoDetalleCliente,classes);//this.detalleclienteLogic.getDetalleCliente()
						
		} else if(Constantes.ISUSAEJBREMOTE) {
						
		} else if(Constantes.ISUSAEJBHOME) {
		}
		//ARCHITECTURE
		
		//ACTUALIZA VARIABLES DEFECTO DESDE LOGIC A RETURN GENERAL Y LUEGO A BEAN
		//this.setVariablesObjetoReturnGeneralToBeanDetalleCliente(this.detalleclienteReturnGeneral,this.detalleclienteBean,false);
		
		if(this.detalleclienteReturnGeneral.getConRecargarPropiedades()) {
			//INICIALIZA VARIABLES COMBOS NORMALES (FK)
			this.setVariablesObjetoActualToFormularioForeignKeyDetalleCliente(this.detalleclienteReturnGeneral.getDetalleCliente());
			
			//INICIALIZA VARIABLES NORMALES A FORMULARIO(SIN FK)
			this.setVariablesObjetoActualToFormularioDetalleCliente(this.detalleclienteReturnGeneral.getDetalleCliente());
		}
		
		if(this.detalleclienteReturnGeneral.getConRecargarRelaciones()) {
			//INICIALIZA VARIABLES RELACIONES A FORMULARIO
			this.setVariablesRelacionesObjetoActualToFormularioDetalleCliente(this.detalleclienteReturnGeneral.getDetalleCliente(),classes);//this.detalleclienteBean);	
		}
			
		//ACTUALIZA VARIABLES FORMULARIO A OBJETO ACTUAL (PARA NUEVO TABLA O GUARDAR CAMBIOS
		if(esNuevoGuardarCambios) {
			this.setVariablesFormularioToObjetoActualDetalleCliente(this.detallecliente,false);
		}
		
		//INICIALIZA VARIABLES COMBOS DEFAULT DEL PROYECTO(|DEFAULT para FK)
		//this.setVariablesDefaultCombosForeignKeyDetalleCliente();
		
		//INICIALIZA VARIABLES COMBOS PARAMETRO DEL PROYECTO(|VALORPARAM Era para ParametroModulo, ahora en logic)
		//this.setVariablesParametroCombosForeignKeyDetalleCliente();						
		
		if(!esNuevoGuardarCambios) {
			//INICIALIZA VARIABLES POR OPCION MENU
			this.arrDatoGeneral= new  ArrayList<DatoGeneral>();
			this.arrDatoGeneralNo= new  ArrayList<String>();
			
			DetalleClienteBeanSwingJInternalFrameAdditional.RecargarFormDetalleCliente(this,"NUEVO_PREPARAR","",this.arrDatoGeneral);
		
			//NO FUNCIONA BINDINGS			
			this.inicializarActualizarBindingDetalleCliente(false);
						
			if(detalleclienteSessionBean.getConGuardarRelaciones()) {
				//DEBERIA YA ESTAR CARGADO LOS COMBOS Y SI SE NECESITA ALGO MAS SE DEBE CREAR FUNCION LIMITADA
				//SI DEBE TRAER Y RESETEAR TABLA
				

				if(this.jInternalFrameDetalleFormDetalleCliente.detallerecapBeanSwingJInternalFrame!=null && this.jInternalFrameDetalleFormDetalleCliente.detallerecapBeanSwingJInternalFrame.detallerecapSessionBean.getEsGuardarRelacionado() && DetalleRecapJInternalFrame.ESTA_CARGADO_PORPARTE) {
					this.jButtonDetalleRecapActionPerformed(null,-1,false,true,null);
				}				
			}
				
			//SI ES MANUAL
			if(DetalleClienteJInternalFrame.ISBINDING_MANUAL) {
				//this.inicializarActualizarBindingManualDetalleCliente();
			}
			
			this.actualizarVisualTableDatosDetalleCliente();
			
			this.jTableDatosDetalleCliente.setRowSelectionInterval(this.getIndiceNuevoDetalleCliente(), this.getIndiceNuevoDetalleCliente());
			
			this.seleccionarFilaTablaDetalleClienteActual();
						
			this.actualizarEstadoCeldasBotonesDetalleCliente("a", isGuardarCambiosEnLote, isEsMantenimientoRelacionado);			
		
		}		
	}
	
