	public void jButtonCopiarDetalleClienteActionPerformed(ActionEvent evt) throws Exception {   
		try {
			
			Boolean conSeleccionarFilaTabla=false;
			Integer iNumRowsSeleccionados=0;
			int[] intSelectedRows =null;
			int intSelectedRow =0;
			
			this.estaModoCopiar=true;
			
			ArrayList<DetalleCliente> detalleclientesSeleccionados=new ArrayList<DetalleCliente>();									
		
			DetalleCliente detalleclienteOrigen=new DetalleCliente();
			DetalleCliente detalleclienteDestino=new DetalleCliente();
				
			detalleclientesSeleccionados=this.getDetalleClientesSeleccionados(false);
			
			if(conSeleccionarFilaTabla) {
				iNumRowsSeleccionados=this.jTableDatosDetalleCliente.getSelectedRows().length;	
			}
			
			if(iNumRowsSeleccionados.equals(2) || detalleclientesSeleccionados.size()==2) {
				if(conSeleccionarFilaTabla) {
					intSelectedRows =this.jTableDatosDetalleCliente.getSelectedRows();
					intSelectedRow = intSelectedRows[0];	
									
					//ARCHITECTURE
					if(Constantes.ISUSAEJBLOGICLAYER) {
						detalleclienteOrigen =(DetalleCliente) this.detalleclienteLogic.getDetalleClientes().toArray()[this.jTableDatosDetalleCliente.convertRowIndexToModel(intSelectedRow)];
					} else if(Constantes.ISUSAEJBREMOTE || Constantes.ISUSAEJBHOME) {
						detalleclienteOrigen =(DetalleCliente) this.detalleclientes.toArray()[this.jTableDatosDetalleCliente.convertRowIndexToModel(intSelectedRow)];
					}
					//ARCHITECTURE
					
					intSelectedRow = intSelectedRows[1];
					
					//ARCHITECTURE
					if(Constantes.ISUSAEJBLOGICLAYER) {
						detalleclienteDestino =(DetalleCliente) this.detalleclienteLogic.getDetalleClientes().toArray()[this.jTableDatosDetalleCliente.convertRowIndexToModel(intSelectedRow)];
					} else if(Constantes.ISUSAEJBREMOTE || Constantes.ISUSAEJBHOME) {
						detalleclienteDestino =(DetalleCliente) this.detalleclientes.toArray()[this.jTableDatosDetalleCliente.convertRowIndexToModel(intSelectedRow)];
					}
					//ARCHITECTURE
				}
				
				detalleclienteOrigen =detalleclientesSeleccionados.get(0);
				detalleclienteDestino =detalleclientesSeleccionados.get(1);
				
				this.setCopiarVariablesObjetosDetalleCliente(detalleclienteOrigen,detalleclienteDestino,true,false);
				
				detalleclienteDestino.setsType("DUPLICADO");
				
				//ARCHITECTURE
				if(Constantes.ISUSAEJBLOGICLAYER) {
					actualizarLista(detalleclienteDestino,detalleclienteLogic.getDetalleClientes());					
				} else if(Constantes.ISUSAEJBREMOTE||Constantes.ISUSAEJBHOME) {
					actualizarLista(detalleclienteDestino,detalleclientes);
				}
				//ARCHITECTURE
				
				this.inicializarActualizarBindingTablaDetalleCliente(false);
				
				//this.jTableDatosDetalleCliente.setRowSelectionInterval(this.getIndiceNuevoDetalleCliente(), this.getIndiceNuevoDetalleCliente());
				
				int iLastRow =  this.jTableDatosDetalleCliente.getRowCount () - 1;
				Rectangle rectangle = this.jTableDatosDetalleCliente.getCellRect(iLastRow, 0, true);
				
				this.jTableDatosDetalleCliente.scrollRectToVisible(rectangle);
				
				//FILA TOTALES
				if(this.conTotales) {
					//this.crearFilaTotales();
					
					this.inicializarActualizarBindingTablaDetalleCliente(false);
				}
			} else {
				throw new Exception("DEBEN ESTAR SELECCIONADOS 2 REGISTROS");
			}
		} catch(Exception e) {
			FuncionesSwing.manageException(this, e,logger,DetalleClienteConstantesFunciones.CLASSNAME);
		
		}  finally {
			this.estaModoCopiar=false;
		}
	}
	
