	public void buscarPorId(Long idActual) throws Exception {    		
		try {
			if(Constantes.ISUSAEJBLOGICLAYER) {
				this.detalleclienteLogic.getNewConnexionToDeep("");
			}
			
			this.idActual=idActual;
			this.iNumeroPaginacionPagina=0;
			
			this.procesarBusqueda("PorId");
				    	
			//NO FUNCIONA BINDINGS
			this.inicializarActualizarBindingDetalleCliente(false);
			
			//SI ES MANUAL
			if(DetalleClienteJInternalFrame.ISBINDING_MANUAL) {
				//this.inicializarActualizarBindingManualDetalleCliente();
			}	
			
			if(Constantes.ISUSAEJBLOGICLAYER) {
				this.detalleclienteLogic.commitNewConnexionToDeep();
			}
			
		} catch(Exception e) {
			
			if(Constantes.ISUSAEJBLOGICLAYER) {
				this.detalleclienteLogic.rollbackNewConnexionToDeep();
			}
			
			FuncionesSwing.manageException(this, e,logger);
			
		} finally {
			if(Constantes.ISUSAEJBLOGICLAYER) {
				this.detalleclienteLogic.closeNewConnexionToDeep();
			}
		}
	}
	
