	public void aumentarTamanioFilaNuevaTablaDetalleCliente() throws Exception {		
		Dimension dimensionMinimum=this.jTableDatosDetalleCliente.getMinimumSize();
		Dimension dimensionMaximum=this.jTableDatosDetalleCliente.getMaximumSize();
		Dimension dimensionPreferred=this.jTableDatosDetalleCliente.getPreferredSize();
	
		double iHeightConFilaNueva=dimensionPreferred.getHeight();
		
		iHeightConFilaNueva+=this.jTableDatosDetalleCliente.getRowHeight();
		
		dimensionMinimum.setSize(dimensionMinimum.getWidth(),iHeightConFilaNueva);
		dimensionMaximum.setSize(dimensionMaximum.getWidth(),iHeightConFilaNueva);
		dimensionPreferred.setSize(dimensionPreferred.getWidth(),iHeightConFilaNueva);
		
		this.jTableDatosDetalleCliente.setMinimumSize(dimensionMinimum);
		this.jTableDatosDetalleCliente.setMaximumSize(dimensionMaximum);
		this.jTableDatosDetalleCliente.setPreferredSize(dimensionPreferred);	
	}
	
