	public void jButtonIdActionPerformed(ActionEvent evt,int rowIndex,Boolean esRelaciones,Boolean esEliminar) { 
		try {
			if(!esEliminar) {
				this.estaModoSeleccionar=true;
				
				//this.isEsNuevoDetalleCliente=false;
					
				DetalleClienteBeanSwingJInternalFrameAdditional.procesarEventosVista(this.parametroGeneralUsuario,this.moduloActual,this.opcionActual,this.usuarioActual,this,FuncionTipo.BEFORE,ControlTipo.FORM,EventoTipo.LOAD,EventoSubTipo.SELECTED,"FORM",this.detallecliente,new Object(),this.detalleclienteParameterGeneral,this.detalleclienteReturnGeneral);
			
				if(this.detalleclienteSessionBean.getConGuardarRelaciones()) {
					this.dStart=(double)System.currentTimeMillis();
				}
				
				if(this.jInternalFrameDetalleFormDetalleCliente==null) {
					this.inicializarFormDetalle();
				}
				
				this.inicializarInvalidValues();
				
				int intSelectedRow = 0;
				
				if(rowIndex>=0) {
					intSelectedRow=rowIndex;
					this.jTableDatosDetalleCliente.getSelectionModel().setSelectionInterval(intSelectedRow, intSelectedRow);
				} else {	
					intSelectedRow=this.jTableDatosDetalleCliente.getSelectedRow();	       
				}
				
				//ARCHITECTURE
				if(Constantes.ISUSAEJBLOGICLAYER) {
					this.detallecliente =(DetalleCliente) this.detalleclienteLogic.getDetalleClientes().toArray()[this.jTableDatosDetalleCliente.convertRowIndexToModel(intSelectedRow)];
				} else if(Constantes.ISUSAEJBREMOTE || Constantes.ISUSAEJBHOME ) {
					this.detallecliente =(DetalleCliente) this.detalleclientes.toArray()[this.jTableDatosDetalleCliente.convertRowIndexToModel(intSelectedRow)];
				}
				//ARCHITECTURE
				
				//PUEDE SER PARA DUPLICADO O NUEVO TABLA
				
				if(this.detallecliente.getsType().equals("DUPLICADO")
				   || this.detallecliente.getsType().equals("NUEVO_GUARDAR_CAMBIOS")) {
					
					this.isEsNuevoDetalleCliente=true;
				
				} else {
					this.isEsNuevoDetalleCliente=false;	
				}
				
				//CONTROL VERSION ANTERIOR
				/*
				if(!this.detalleclienteSessionBean.getEsGuardarRelacionado()) {
					if(this.detallecliente.getId()>=0 && !this.detallecliente.getIsNew()) {						
						this.isEsNuevoDetalleCliente=false;
						
					} else {
						this.isEsNuevoDetalleCliente=true;
					}
					
				} else {
					//CONTROLAR PARA RELACIONADO
				}
				*/
				
				//ESTABLECE SI ES RELACIONADO O NO 
				this.habilitarDeshabilitarTipoMantenimientoDetalleCliente(esRelaciones);						
				
				this.seleccionarDetalleCliente(evt,null,rowIndex);
				
				//SELECCIONA ACTUAL PERO AUN NO SE HA INGRESADO AL SISTEMA
				//SE DESHABILITA POR GUARDAR CAMBIOS
				/*
				if(this.detallecliente.getId()<0) {
					this.isEsNuevoDetalleCliente=true;
				}
				*/
				
				if(!this.esParaBusquedaForeignKey) {
					this.modificarDetalleCliente(evt,rowIndex,esRelaciones);
				} else {
					this.seleccionarDetalleCliente(evt,rowIndex);
				}	
				
				if(this.detalleclienteSessionBean.getConGuardarRelaciones()) {
					this.dEnd=(double)System.currentTimeMillis();					
					this.dDif=this.dEnd - this.dStart;
					
					if(Constantes.ISDEVELOPING) {
						System.out.println("Tiempo(ms) Seleccion DetalleCliente: " + this.dDif); 
					}
				}								
				
				DetalleClienteBeanSwingJInternalFrameAdditional.procesarEventosVista(this.parametroGeneralUsuario,this.moduloActual,this.opcionActual,this.usuarioActual,this,FuncionTipo.AFTER,ControlTipo.FORM,EventoTipo.LOAD,EventoSubTipo.SELECTED,"FORM",this.detallecliente,new Object(),this.detalleclienteParameterGeneral,this.detalleclienteReturnGeneral);
				
			} else {
				this.estaModoEliminarGuardarCambios=true;
				
				this.seleccionarDetalleCliente(evt,null,rowIndex);
				
				if(this.permiteMantenimiento(this.detallecliente)) {
					if(this.detallecliente.getId()>0) {
						this.detallecliente.setIsDeleted(true);
						
						this.detalleclientesEliminados.add(this.detallecliente);
					}
					
					if(Constantes.ISUSAEJBLOGICLAYER) {				
						this.detalleclienteLogic.getDetalleClientes().remove(this.detallecliente);
					} else if(Constantes.ISUSAEJBREMOTE||Constantes.ISUSAEJBHOME) {
						this.detalleclientes.remove(this.detallecliente);				
					}
					
					
					((DetalleClienteModel) this.jTableDatosDetalleCliente.getModel()).fireTableRowsDeleted(rowIndex,rowIndex);
					
					this.actualizarFilaTotales();
					
					this.inicializarActualizarBindingTablaDetalleCliente(false);					
				}								
			}
			
		} catch(Exception e) {
			FuncionesSwing.manageException2(this, e,logger,DetalleClienteConstantesFunciones.CLASSNAME);
			
		} finally {
			this.estaModoSeleccionar=false;				
			this.estaModoEliminarGuardarCambios=false;
		}
	}
	
	
