	public void jButtonDetalleRecapActionPerformed(ActionEvent evt,int rowIndex,Boolean conInicializar,Boolean esRelacionado,DetalleCliente detallecliente) throws Exception {
			try {

				if(this.jInternalFrameDetalleFormDetalleCliente==null) {
					this.inicializarFormDetalle();
				}

				if(Constantes.ISUSAEJBLOGICLAYER) {
					this.detalleclienteLogic.getNewConnexionToDeep("");
				}

				int intSelectedRow =rowIndex;

				if(intSelectedRow!=-1) {
					//ARCHITECTURE
					if(Constantes.ISUSAEJBLOGICLAYER) {
						this.detallecliente = (DetalleCliente)this.detalleclienteLogic.getDetalleClientes().toArray()[this.jTableDatosDetalleCliente.convertRowIndexToModel(intSelectedRow)];
					} else if(Constantes.ISUSAEJBREMOTE) {
						this.detallecliente = (DetalleCliente)this.detalleclientes.toArray()[this.jTableDatosDetalleCliente.convertRowIndexToModel(intSelectedRow)];
					}
					//ARCHITECTURE
				} else {
					if(detallecliente!=null) {
						this.detallecliente = detallecliente;
					} else {
						this.detallecliente = new DetalleCliente();
					}
				}

				if(this.isTienePermisosDetalleRecap && this.permiteMantenimiento(this.detallecliente)) {
					DetalleRecapBeanSwingJInternalFrame detallerecapBeanSwingJInternalFrame=null;

					if(conInicializar) {
						this.jInternalFrameDetalleFormDetalleCliente.detallerecapBeanSwingJInternalFramePopup=new DetalleRecapBeanSwingJInternalFrame(false,false,this.jDesktopPane,this.usuarioActual,this.resumenUsuarioActual,this.moduloActual,this.opcionActual,this.parametroGeneralSg,this.parametroGeneralUsuario,PaginaTipo.SECUNDARIO,false,false,true,false);
						this.jInternalFrameDetalleFormDetalleCliente.detallerecapBeanSwingJInternalFramePopup.setJInternalFrameParent(this);

						detallerecapBeanSwingJInternalFrame=this.jInternalFrameDetalleFormDetalleCliente.detallerecapBeanSwingJInternalFramePopup;
					} else {
						detallerecapBeanSwingJInternalFrame=this.jInternalFrameDetalleFormDetalleCliente.detallerecapBeanSwingJInternalFrame;
					}

					List<DetalleCliente> detalleclientes=new ArrayList<DetalleCliente>();
					detalleclientes.add(this.detallecliente);
					if(!esRelacionado) {
						//detallerecapBeanSwingJInternalFrame.detallerecapSessionBean.setConGuardarRelaciones(false);
						//detallerecapBeanSwingJInternalFrame.detallerecapSessionBean.setEsGuardarRelacionado(false);
					}

					//DESHABILITA TEMPORALMENTE EVENTOS CHANGE DE TEXTOS,COMBOS,ETC
					detallerecapBeanSwingJInternalFrame.estaModoSeleccionar=true;
					this.jInternalFrameDetalleFormDetalleCliente.cargarDetalleRecapBeanSwingJInternalFrame(detalleclientes,this.detallecliente,detallerecapBeanSwingJInternalFrame,/*conInicializar,*/detallerecapBeanSwingJInternalFrame.detallerecapSessionBean.getConGuardarRelaciones(),detallerecapBeanSwingJInternalFrame.detallerecapSessionBean.getEsGuardarRelacionado());
					detallerecapBeanSwingJInternalFrame.estaModoSeleccionar=false;


					if(!esRelacionado) {
						detallerecapBeanSwingJInternalFrame.actualizarEstadoPanelsDetalleRecap("no_relacionado");

						detallerecapBeanSwingJInternalFrame.redimensionarTablaDatosConTamanio(DetalleRecapConstantesFunciones.ITAMANIOFILATABLA + (DetalleRecapConstantesFunciones.ITAMANIOFILATABLA/2));

						detallerecapBeanSwingJInternalFrame.setLocation(xOffset*(openFrameCount + Constantes.INUM_MAX_VENTANAS_REL_X), yOffset*(openFrameCount+Constantes.INUM_MAX_VENTANAS_REL_Y));

						TitledBorder titledBorderDetalleCliente=(TitledBorder)this.jScrollPanelDatosDetalleCliente.getBorder();
						TitledBorder titledBorderDetalleRecap=(TitledBorder)detallerecapBeanSwingJInternalFrame.jScrollPanelDatosDetalleRecap.getBorder();

						titledBorderDetalleRecap.setTitle(titledBorderDetalleCliente.getTitle() + " -> Detalle Recap");

						if(!Constantes.CON_VARIAS_VENTANAS) {
							MainJFrame.cerrarJInternalFramesExistentes(this.jDesktopPane,detallerecapBeanSwingJInternalFrame);
						}

						detallerecapBeanSwingJInternalFrame.setVisible(true);

						this.jDesktopPane.add(detallerecapBeanSwingJInternalFrame);

						detallerecapBeanSwingJInternalFrame.setSelected(true);
					}

				} else {
					if(!this.detalleclienteSessionBean.getConGuardarRelaciones()) {
						JOptionPane.showMessageDialog(this,"NO TIENE PERMISOS PARA USAR LA FUNCIONALIDAD DE Detalle Recap",Constantes.SERROR,JOptionPane.ERROR_MESSAGE);
					}
				}
				if(Constantes.ISUSAEJBLOGICLAYER) {
					this.detalleclienteLogic.commitNewConnexionToDeep();
				}

			}

			catch(Exception e) {
				if(Constantes.ISUSAEJBLOGICLAYER) {
					this.detalleclienteLogic.rollbackNewConnexionToDeep();
				}


				FuncionesSwing.manageException2(this, e,logger,DetalleClienteConstantesFunciones.CLASSNAME);
			} finally { 
				if(Constantes.ISUSAEJBLOGICLAYER) {
					this.detalleclienteLogic.closeNewConnexionToDeep();
				}

			}
		}

	
	
	
