	public ArrayList<DetalleCliente> getDetalleClientesSeleccionados(Boolean conSeleccionarTodosAutomatico) throws Exception {
		ArrayList<DetalleCliente> detalleclientesSeleccionados=new ArrayList<DetalleCliente>();
		Boolean existe=false;
		
		if(!this.esParaAccionDesdeFormularioDetalleCliente) {
			if(Constantes.ISUSAEJBLOGICLAYER) {	
				
				for(DetalleCliente detalleclienteAux:detalleclienteLogic.getDetalleClientes()) {
					if(detalleclienteAux.getIsSelected()) {
						detalleclientesSeleccionados.add(detalleclienteAux);				
					}
				}
				
			} else if(Constantes.ISUSAEJBREMOTE||Constantes.ISUSAEJBHOME) {
				for(DetalleCliente detalleclienteAux:this.detalleclientes) {
					if(detalleclienteAux.getIsSelected()) {
						detalleclientesSeleccionados.add(detalleclienteAux);				
					}
				}
			}
			
			if(detalleclientesSeleccionados.size()>0) {
				existe=true;
			}
			
			//SI NO ESTA NINGUNO SELECCIONADO SE SELECCIONA TODOS
			if(!existe) {
				if(conSeleccionarTodosAutomatico) {
					if(Constantes.ISUSAEJBLOGICLAYER) {				
						detalleclientesSeleccionados.addAll(this.detalleclienteLogic.getDetalleClientes());
					} else if(Constantes.ISUSAEJBREMOTE||Constantes.ISUSAEJBHOME) {
						detalleclientesSeleccionados.addAll(this.detalleclientes);				
					}
				}
			}
		} else {
			detalleclientesSeleccionados.add(this.detallecliente);
		}
		
		return detalleclientesSeleccionados;
	}
	
