		ArrayList<Classe> classes=new ArrayList<Classe>();
		List<FormularioRentaExtra> formulariorentaextrasRespaldo=null;
		
		classes=FormularioRentaExtraConstantesFunciones.getClassesRelationshipsOfFormularioRentaExtra(new ArrayList<Classe>(),DeepLoadType.NONE,false);
					
		this.datosDeep=new DatosDeep();
		
		this.datosDeep.setIsDeep(false);
		this.datosDeep.setDeepLoadType(DeepLoadType.INCLUDE);
		this.datosDeep.setClases(classes);
			
		this.datosCliente.setDatosDeepParametros(false, DeepLoadType.INCLUDE, classes, "");
		this.datosCliente.setIsConDeep(true);	
		this.datosCliente.setIsConExportar(false);
		
						
		this.formulariorentaextraLogic.setDatosCliente(this.datosCliente);
		this.formulariorentaextraLogic.setDatosDeep(this.datosDeep);
		this.formulariorentaextraLogic.setIsConDeep(true);
		
		formulariorentaextrasRespaldo=this.formulariorentaextraLogic.getFormularioRentaExtras();
		
		this.formulariorentaextraLogic.setFormularioRentaExtras(formulariorentaextrasParaReportes);	
		this.formulariorentaextraLogic.deepLoadsWithConnection(false, DeepLoadType.INCLUDE, classes,"");
		
		formulariorentaextrasParaReportes=this.formulariorentaextraLogic.getFormularioRentaExtras();
