	public void buscarPorId(Long idActual) throws Exception {    		
		try {
			if(Constantes.ISUSAEJBLOGICLAYER) {
				this.formulariorentaextraLogic.getNewConnexionToDeep("");
			}
			
			this.idActual=idActual;
			this.iNumeroPaginacionPagina=0;
			
			this.procesarBusqueda("PorId");
				    	
			//NO FUNCIONA BINDINGS
			this.inicializarActualizarBindingFormularioRentaExtra(false);
			
			//SI ES MANUAL
			if(FormularioRentaExtraJInternalFrame.ISBINDING_MANUAL) {
				//this.inicializarActualizarBindingManualFormularioRentaExtra();
			}	
			
			if(Constantes.ISUSAEJBLOGICLAYER) {
				this.formulariorentaextraLogic.commitNewConnexionToDeep();
			}
			
		} catch(Exception e) {
			
			if(Constantes.ISUSAEJBLOGICLAYER) {
				this.formulariorentaextraLogic.rollbackNewConnexionToDeep();
			}
			
			FuncionesSwing.manageException(this, e,logger);
			
		} finally {
			if(Constantes.ISUSAEJBLOGICLAYER) {
				this.formulariorentaextraLogic.closeNewConnexionToDeep();
			}
		}
	}
	
