	public ArrayList<FormularioRentaExtra> getFormularioRentaExtrasSeleccionados(Boolean conSeleccionarTodosAutomatico) throws Exception {
		ArrayList<FormularioRentaExtra> formulariorentaextrasSeleccionados=new ArrayList<FormularioRentaExtra>();
		Boolean existe=false;
		
		if(!this.esParaAccionDesdeFormularioFormularioRentaExtra) {
			if(Constantes.ISUSAEJBLOGICLAYER) {	
				
				for(FormularioRentaExtra formulariorentaextraAux:formulariorentaextraLogic.getFormularioRentaExtras()) {
					if(formulariorentaextraAux.getIsSelected()) {
						formulariorentaextrasSeleccionados.add(formulariorentaextraAux);				
					}
				}
				
			} else if(Constantes.ISUSAEJBREMOTE||Constantes.ISUSAEJBHOME) {
				for(FormularioRentaExtra formulariorentaextraAux:this.formulariorentaextras) {
					if(formulariorentaextraAux.getIsSelected()) {
						formulariorentaextrasSeleccionados.add(formulariorentaextraAux);				
					}
				}
			}
			
			if(formulariorentaextrasSeleccionados.size()>0) {
				existe=true;
			}
			
			//SI NO ESTA NINGUNO SELECCIONADO SE SELECCIONA TODOS
			if(!existe) {
				if(conSeleccionarTodosAutomatico) {
					if(Constantes.ISUSAEJBLOGICLAYER) {				
						formulariorentaextrasSeleccionados.addAll(this.formulariorentaextraLogic.getFormularioRentaExtras());
					} else if(Constantes.ISUSAEJBREMOTE||Constantes.ISUSAEJBHOME) {
						formulariorentaextrasSeleccionados.addAll(this.formulariorentaextras);				
					}
				}
			}
		} else {
			formulariorentaextrasSeleccionados.add(this.formulariorentaextra);
		}
		
		return formulariorentaextrasSeleccionados;
	}
	
