	public void exportarTextoFormularioRentaExtrasSeleccionados() throws Exception {
		ArrayList<FormularioRentaExtra> formulariorentaextrasSeleccionados=new ArrayList<FormularioRentaExtra>();		
		
		formulariorentaextrasSeleccionados=this.getFormularioRentaExtrasSeleccionados(true);
		
		String sTipo=Funciones2.getTipoExportar(this.parametroGeneralUsuario);
		Boolean conCabecera=this.parametroGeneralUsuario.getcon_exportar_cabecera();
		String sDelimiter=Funciones2.getTipoDelimiter(this.parametroGeneralUsuario);
		String sPath=this.parametroGeneralUsuario.getpath_exportar()+"formulariorentaextra."+"txt";//Funciones2.getTipoExtensionArchivoExportar(this.parametroGeneralUsuario);
		
		String sFilaCabecera="";
		String sFilaDatos="";
		
		BufferedWriter bufferedWriter = null;
		FileWriter fileWriter=null;
		
		fileWriter=new FileWriter(sPath);
		
		bufferedWriter = new BufferedWriter(fileWriter);
		
		try {
			if(conCabecera) {
				sFilaCabecera=this.getFilaCabeceraExportarFormularioRentaExtra(sDelimiter);
				
				bufferedWriter.write(sFilaCabecera);
			}				
			
			for(FormularioRentaExtra formulariorentaextraAux:formulariorentaextrasSeleccionados) {
				sFilaDatos=this.getFilaDatosExportarFormularioRentaExtra(formulariorentaextraAux,sDelimiter);
				
				bufferedWriter.write(sFilaDatos);
				
				//formulariorentaextraAux.setsDetalleGeneralEntityReporte(formulariorentaextraAux.toString());			
			}
			
			bufferedWriter.close();
			
			Constantes2.S_PATH_ULTIMO_ARCHIVO=sPath;
			
			if(this.parametroGeneralUsuario.getcon_mensaje_confirmacion() && !this.formulariorentaextraSessionBean.getEsGuardarRelacionado()) {//Constantes.ISMOSTRARMENSAJESMANTENIMIENTO && 
				JOptionPane.showMessageDialog(this,"EXPORTADO CORRECTAMENTE:"+sPath,"MANTENIMIENTO DE Formulario Renta Extra",JOptionPane.INFORMATION_MESSAGE);
			}
		} catch (Exception e) {
			throw e;
			
		} finally {
       		if (bufferedWriter != null) {
				bufferedWriter.close();
			}
		}
	}
	
