	public void aumentarTamanioFilaNuevaTablaParametroFormularioIva() throws Exception {		
		Dimension dimensionMinimum=this.jTableDatosParametroFormularioIva.getMinimumSize();
		Dimension dimensionMaximum=this.jTableDatosParametroFormularioIva.getMaximumSize();
		Dimension dimensionPreferred=this.jTableDatosParametroFormularioIva.getPreferredSize();
	
		double iHeightConFilaNueva=dimensionPreferred.getHeight();
		
		iHeightConFilaNueva+=this.jTableDatosParametroFormularioIva.getRowHeight();
		
		dimensionMinimum.setSize(dimensionMinimum.getWidth(),iHeightConFilaNueva);
		dimensionMaximum.setSize(dimensionMaximum.getWidth(),iHeightConFilaNueva);
		dimensionPreferred.setSize(dimensionPreferred.getWidth(),iHeightConFilaNueva);
		
		this.jTableDatosParametroFormularioIva.setMinimumSize(dimensionMinimum);
		this.jTableDatosParametroFormularioIva.setMaximumSize(dimensionMaximum);
		this.jTableDatosParametroFormularioIva.setPreferredSize(dimensionPreferred);	
	}
	
