	public void jButtonIdActionPerformed(ActionEvent evt,int rowIndex,Boolean esRelaciones,Boolean esEliminar) { 
		try {
			if(!esEliminar) {
				this.estaModoSeleccionar=true;
				
				//this.isEsNuevoParametroFormularioIva=false;
					
				ParametroFormularioIvaBeanSwingJInternalFrameAdditional.procesarEventosVista(this.parametroGeneralUsuario,this.moduloActual,this.opcionActual,this.usuarioActual,this,FuncionTipo.BEFORE,ControlTipo.FORM,EventoTipo.LOAD,EventoSubTipo.SELECTED,"FORM",this.parametroformularioiva,new Object(),this.parametroformularioivaParameterGeneral,this.parametroformularioivaReturnGeneral);
			
				if(this.parametroformularioivaSessionBean.getConGuardarRelaciones()) {
					this.dStart=(double)System.currentTimeMillis();
				}
				
				if(this.jInternalFrameDetalleFormParametroFormularioIva==null) {
					this.inicializarFormDetalle();
				}
				
				this.inicializarInvalidValues();
				
				int intSelectedRow = 0;
				
				if(rowIndex>=0) {
					intSelectedRow=rowIndex;
					this.jTableDatosParametroFormularioIva.getSelectionModel().setSelectionInterval(intSelectedRow, intSelectedRow);
				} else {	
					intSelectedRow=this.jTableDatosParametroFormularioIva.getSelectedRow();	       
				}
				
				//ARCHITECTURE
				if(Constantes.ISUSAEJBLOGICLAYER) {
					this.parametroformularioiva =(ParametroFormularioIva) this.parametroformularioivaLogic.getParametroFormularioIvas().toArray()[this.jTableDatosParametroFormularioIva.convertRowIndexToModel(intSelectedRow)];
				} else if(Constantes.ISUSAEJBREMOTE || Constantes.ISUSAEJBHOME ) {
					this.parametroformularioiva =(ParametroFormularioIva) this.parametroformularioivas.toArray()[this.jTableDatosParametroFormularioIva.convertRowIndexToModel(intSelectedRow)];
				}
				//ARCHITECTURE
				
				//PUEDE SER PARA DUPLICADO O NUEVO TABLA
				
				if(this.parametroformularioiva.getsType().equals("DUPLICADO")
				   || this.parametroformularioiva.getsType().equals("NUEVO_GUARDAR_CAMBIOS")) {
					
					this.isEsNuevoParametroFormularioIva=true;
				
				} else {
					this.isEsNuevoParametroFormularioIva=false;	
				}
				
				//CONTROL VERSION ANTERIOR
				/*
				if(!this.parametroformularioivaSessionBean.getEsGuardarRelacionado()) {
					if(this.parametroformularioiva.getId()>=0 && !this.parametroformularioiva.getIsNew()) {						
						this.isEsNuevoParametroFormularioIva=false;
						
					} else {
						this.isEsNuevoParametroFormularioIva=true;
					}
					
				} else {
					//CONTROLAR PARA RELACIONADO
				}
				*/
				
				//ESTABLECE SI ES RELACIONADO O NO 
				this.habilitarDeshabilitarTipoMantenimientoParametroFormularioIva(esRelaciones);						
				
				this.seleccionarParametroFormularioIva(evt,null,rowIndex);
				
				//SELECCIONA ACTUAL PERO AUN NO SE HA INGRESADO AL SISTEMA
				//SE DESHABILITA POR GUARDAR CAMBIOS
				/*
				if(this.parametroformularioiva.getId()<0) {
					this.isEsNuevoParametroFormularioIva=true;
				}
				*/
				
				if(!this.esParaBusquedaForeignKey) {
					this.modificarParametroFormularioIva(evt,rowIndex,esRelaciones);
				} else {
					this.seleccionarParametroFormularioIva(evt,rowIndex);
				}	
				
				if(this.parametroformularioivaSessionBean.getConGuardarRelaciones()) {
					this.dEnd=(double)System.currentTimeMillis();					
					this.dDif=this.dEnd - this.dStart;
					
					if(Constantes.ISDEVELOPING) {
						System.out.println("Tiempo(ms) Seleccion ParametroFormularioIva: " + this.dDif); 
					}
				}								
				
				ParametroFormularioIvaBeanSwingJInternalFrameAdditional.procesarEventosVista(this.parametroGeneralUsuario,this.moduloActual,this.opcionActual,this.usuarioActual,this,FuncionTipo.AFTER,ControlTipo.FORM,EventoTipo.LOAD,EventoSubTipo.SELECTED,"FORM",this.parametroformularioiva,new Object(),this.parametroformularioivaParameterGeneral,this.parametroformularioivaReturnGeneral);
				
			} else {
				this.estaModoEliminarGuardarCambios=true;
				
				this.seleccionarParametroFormularioIva(evt,null,rowIndex);
				
				if(this.permiteMantenimiento(this.parametroformularioiva)) {
					if(this.parametroformularioiva.getId()>0) {
						this.parametroformularioiva.setIsDeleted(true);
						
						this.parametroformularioivasEliminados.add(this.parametroformularioiva);
					}
					
					if(Constantes.ISUSAEJBLOGICLAYER) {				
						this.parametroformularioivaLogic.getParametroFormularioIvas().remove(this.parametroformularioiva);
					} else if(Constantes.ISUSAEJBREMOTE||Constantes.ISUSAEJBHOME) {
						this.parametroformularioivas.remove(this.parametroformularioiva);				
					}
					
					
					((ParametroFormularioIvaModel) this.jTableDatosParametroFormularioIva.getModel()).fireTableRowsDeleted(rowIndex,rowIndex);
					
					this.actualizarFilaTotales();
					
					this.inicializarActualizarBindingTablaParametroFormularioIva(false);					
				}								
			}
			
		} catch(Exception e) {
			FuncionesSwing.manageException2(this, e,logger,ParametroFormularioIvaConstantesFunciones.CLASSNAME);
			
		} finally {
			this.estaModoSeleccionar=false;				
			this.estaModoEliminarGuardarCambios=false;
		}
	}
	
	
