	public ArrayList<ParametroFormularioIva> getParametroFormularioIvasSeleccionados(Boolean conSeleccionarTodosAutomatico) throws Exception {
		ArrayList<ParametroFormularioIva> parametroformularioivasSeleccionados=new ArrayList<ParametroFormularioIva>();
		Boolean existe=false;
		
		if(!this.esParaAccionDesdeFormularioParametroFormularioIva) {
			if(Constantes.ISUSAEJBLOGICLAYER) {	
				
				for(ParametroFormularioIva parametroformularioivaAux:parametroformularioivaLogic.getParametroFormularioIvas()) {
					if(parametroformularioivaAux.getIsSelected()) {
						parametroformularioivasSeleccionados.add(parametroformularioivaAux);				
					}
				}
				
			} else if(Constantes.ISUSAEJBREMOTE||Constantes.ISUSAEJBHOME) {
				for(ParametroFormularioIva parametroformularioivaAux:this.parametroformularioivas) {
					if(parametroformularioivaAux.getIsSelected()) {
						parametroformularioivasSeleccionados.add(parametroformularioivaAux);				
					}
				}
			}
			
			if(parametroformularioivasSeleccionados.size()>0) {
				existe=true;
			}
			
			//SI NO ESTA NINGUNO SELECCIONADO SE SELECCIONA TODOS
			if(!existe) {
				if(conSeleccionarTodosAutomatico) {
					if(Constantes.ISUSAEJBLOGICLAYER) {				
						parametroformularioivasSeleccionados.addAll(this.parametroformularioivaLogic.getParametroFormularioIvas());
					} else if(Constantes.ISUSAEJBREMOTE||Constantes.ISUSAEJBHOME) {
						parametroformularioivasSeleccionados.addAll(this.parametroformularioivas);				
					}
				}
			}
		} else {
			parametroformularioivasSeleccionados.add(this.parametroformularioiva);
		}
		
		return parametroformularioivasSeleccionados;
	}
	
