	public void aumentarTamanioFilaNuevaTablaDetallePedidoExpor() throws Exception {		
		Dimension dimensionMinimum=this.jTableDatosDetallePedidoExpor.getMinimumSize();
		Dimension dimensionMaximum=this.jTableDatosDetallePedidoExpor.getMaximumSize();
		Dimension dimensionPreferred=this.jTableDatosDetallePedidoExpor.getPreferredSize();
	
		double iHeightConFilaNueva=dimensionPreferred.getHeight();
		
		iHeightConFilaNueva+=this.jTableDatosDetallePedidoExpor.getRowHeight();
		
		dimensionMinimum.setSize(dimensionMinimum.getWidth(),iHeightConFilaNueva);
		dimensionMaximum.setSize(dimensionMaximum.getWidth(),iHeightConFilaNueva);
		dimensionPreferred.setSize(dimensionPreferred.getWidth(),iHeightConFilaNueva);
		
		this.jTableDatosDetallePedidoExpor.setMinimumSize(dimensionMinimum);
		this.jTableDatosDetallePedidoExpor.setMaximumSize(dimensionMaximum);
		this.jTableDatosDetallePedidoExpor.setPreferredSize(dimensionPreferred);	
	}
	
