	public void jButtonIdActionPerformed(ActionEvent evt,int rowIndex,Boolean esRelaciones,Boolean esEliminar) { 
		try {
			if(!esEliminar) {
				this.estaModoSeleccionar=true;
				
				//this.isEsNuevoDetallePedidoExpor=false;
					
				DetallePedidoExporBeanSwingJInternalFrameAdditional.procesarEventosVista(this.parametroGeneralUsuario,this.moduloActual,this.opcionActual,this.usuarioActual,this,FuncionTipo.BEFORE,ControlTipo.FORM,EventoTipo.LOAD,EventoSubTipo.SELECTED,"FORM",this.detallepedidoexpor,new Object(),this.detallepedidoexporParameterGeneral,this.detallepedidoexporReturnGeneral);
			
				if(this.detallepedidoexporSessionBean.getConGuardarRelaciones()) {
					this.dStart=(double)System.currentTimeMillis();
				}
				
				if(this.jInternalFrameDetalleFormDetallePedidoExpor==null) {
					this.inicializarFormDetalle();
				}
				
				this.inicializarInvalidValues();
				
				int intSelectedRow = 0;
				
				if(rowIndex>=0) {
					intSelectedRow=rowIndex;
					this.jTableDatosDetallePedidoExpor.getSelectionModel().setSelectionInterval(intSelectedRow, intSelectedRow);
				} else {	
					intSelectedRow=this.jTableDatosDetallePedidoExpor.getSelectedRow();	       
				}
				
				//ARCHITECTURE
				if(Constantes.ISUSAEJBLOGICLAYER) {
					this.detallepedidoexpor =(DetallePedidoExpor) this.detallepedidoexporLogic.getDetallePedidoExpors().toArray()[this.jTableDatosDetallePedidoExpor.convertRowIndexToModel(intSelectedRow)];
				} else if(Constantes.ISUSAEJBREMOTE || Constantes.ISUSAEJBHOME ) {
					this.detallepedidoexpor =(DetallePedidoExpor) this.detallepedidoexpors.toArray()[this.jTableDatosDetallePedidoExpor.convertRowIndexToModel(intSelectedRow)];
				}
				//ARCHITECTURE
				
				//PUEDE SER PARA DUPLICADO O NUEVO TABLA
				
				if(this.detallepedidoexpor.getsType().equals("DUPLICADO")
				   || this.detallepedidoexpor.getsType().equals("NUEVO_GUARDAR_CAMBIOS")) {
					
					this.isEsNuevoDetallePedidoExpor=true;
				
				} else {
					this.isEsNuevoDetallePedidoExpor=false;	
				}
				
				//CONTROL VERSION ANTERIOR
				/*
				if(!this.detallepedidoexporSessionBean.getEsGuardarRelacionado()) {
					if(this.detallepedidoexpor.getId()>=0 && !this.detallepedidoexpor.getIsNew()) {						
						this.isEsNuevoDetallePedidoExpor=false;
						
					} else {
						this.isEsNuevoDetallePedidoExpor=true;
					}
					
				} else {
					//CONTROLAR PARA RELACIONADO
				}
				*/
				
				//ESTABLECE SI ES RELACIONADO O NO 
				this.habilitarDeshabilitarTipoMantenimientoDetallePedidoExpor(esRelaciones);						
				
				this.seleccionarDetallePedidoExpor(evt,null,rowIndex);
				
				//SELECCIONA ACTUAL PERO AUN NO SE HA INGRESADO AL SISTEMA
				//SE DESHABILITA POR GUARDAR CAMBIOS
				/*
				if(this.detallepedidoexpor.getId()<0) {
					this.isEsNuevoDetallePedidoExpor=true;
				}
				*/
				
				if(!this.esParaBusquedaForeignKey) {
					this.modificarDetallePedidoExpor(evt,rowIndex,esRelaciones);
				} else {
					this.seleccionarDetallePedidoExpor(evt,rowIndex);
				}	
				
				if(this.detallepedidoexporSessionBean.getConGuardarRelaciones()) {
					this.dEnd=(double)System.currentTimeMillis();					
					this.dDif=this.dEnd - this.dStart;
					
					if(Constantes.ISDEVELOPING) {
						System.out.println("Tiempo(ms) Seleccion DetallePedidoExpor: " + this.dDif); 
					}
				}								
				
				DetallePedidoExporBeanSwingJInternalFrameAdditional.procesarEventosVista(this.parametroGeneralUsuario,this.moduloActual,this.opcionActual,this.usuarioActual,this,FuncionTipo.AFTER,ControlTipo.FORM,EventoTipo.LOAD,EventoSubTipo.SELECTED,"FORM",this.detallepedidoexpor,new Object(),this.detallepedidoexporParameterGeneral,this.detallepedidoexporReturnGeneral);
				
			} else {
				this.estaModoEliminarGuardarCambios=true;
				
				this.seleccionarDetallePedidoExpor(evt,null,rowIndex);
				
				if(this.permiteMantenimiento(this.detallepedidoexpor)) {
					if(this.detallepedidoexpor.getId()>0) {
						this.detallepedidoexpor.setIsDeleted(true);
						
						this.detallepedidoexporsEliminados.add(this.detallepedidoexpor);
					}
					
					if(Constantes.ISUSAEJBLOGICLAYER) {				
						this.detallepedidoexporLogic.getDetallePedidoExpors().remove(this.detallepedidoexpor);
					} else if(Constantes.ISUSAEJBREMOTE||Constantes.ISUSAEJBHOME) {
						this.detallepedidoexpors.remove(this.detallepedidoexpor);				
					}
					
					
					((DetallePedidoExporModel) this.jTableDatosDetallePedidoExpor.getModel()).fireTableRowsDeleted(rowIndex,rowIndex);
					
					this.actualizarFilaTotales();
					
					this.inicializarActualizarBindingTablaDetallePedidoExpor(false);					
				}								
			}
			
		} catch(Exception e) {
			FuncionesSwing.manageException2(this, e,logger,DetallePedidoExporConstantesFunciones.CLASSNAME);
			
		} finally {
			this.estaModoSeleccionar=false;				
			this.estaModoEliminarGuardarCambios=false;
		}
	}
	
	
