	public void actualizarInformacion(String sTipo,Boolean conParametroObjeto,DetallePedidoExpor detallepedidoexporLocal) throws Exception {
		
		if(!conParametroObjeto) {
			if(!this.getEsControlTabla()) {
				detallepedidoexporLocal=this.detallepedidoexpor;
			} else {
				detallepedidoexporLocal=this.detallepedidoexporAnterior;
			}
		}
		
		if(this.permiteMantenimiento(detallepedidoexporLocal)) {
			if(sTipo.equals("EVENTO_CONTROL")) { // || sTipo.equals("EVENTO_NUEVO")
				if(!this.esControlTabla) {
					this.setVariablesFormularioToObjetoActualTodoDetallePedidoExpor(detallepedidoexporLocal,true);
					
					if(detallepedidoexporSessionBean.getConGuardarRelaciones()) {
						this.actualizarRelaciones(detallepedidoexporLocal);
					}
				}
			
			} else if(sTipo.equals("INFO_PADRE")) {
				
				if(this.detallepedidoexporSessionBean.getEsGuardarRelacionado()) {
					this.actualizarRelacionFkPadreActual(detallepedidoexporLocal);
				}
			}
		}
	}
	
