	public ArrayList<DetallePedidoExpor> getDetallePedidoExporsSeleccionados(Boolean conSeleccionarTodosAutomatico) throws Exception {
		ArrayList<DetallePedidoExpor> detallepedidoexporsSeleccionados=new ArrayList<DetallePedidoExpor>();
		Boolean existe=false;
		
		if(!this.esParaAccionDesdeFormularioDetallePedidoExpor) {
			if(Constantes.ISUSAEJBLOGICLAYER) {	
				
				for(DetallePedidoExpor detallepedidoexporAux:detallepedidoexporLogic.getDetallePedidoExpors()) {
					if(detallepedidoexporAux.getIsSelected()) {
						detallepedidoexporsSeleccionados.add(detallepedidoexporAux);				
					}
				}
				
			} else if(Constantes.ISUSAEJBREMOTE||Constantes.ISUSAEJBHOME) {
				for(DetallePedidoExpor detallepedidoexporAux:this.detallepedidoexpors) {
					if(detallepedidoexporAux.getIsSelected()) {
						detallepedidoexporsSeleccionados.add(detallepedidoexporAux);				
					}
				}
			}
			
			if(detallepedidoexporsSeleccionados.size()>0) {
				existe=true;
			}
			
			//SI NO ESTA NINGUNO SELECCIONADO SE SELECCIONA TODOS
			if(!existe) {
				if(conSeleccionarTodosAutomatico) {
					if(Constantes.ISUSAEJBLOGICLAYER) {				
						detallepedidoexporsSeleccionados.addAll(this.detallepedidoexporLogic.getDetallePedidoExpors());
					} else if(Constantes.ISUSAEJBREMOTE||Constantes.ISUSAEJBHOME) {
						detallepedidoexporsSeleccionados.addAll(this.detallepedidoexpors);				
					}
				}
			}
		} else {
			detallepedidoexporsSeleccionados.add(this.detallepedidoexpor);
		}
		
		return detallepedidoexporsSeleccionados;
	}
	
