	public Boolean tieneElementosSeleccionados() throws Exception {
		Boolean tiene=false;
		
		List<DetallePedido> detallepedidosLocal=null;
		
		try	{			
			//ARCHITECTURE
			if(Constantes.ISUSAEJBLOGICLAYER) {
				detallepedidosLocal=this.detallepedidoLogic.getDetallePedidos();
			} else if(Constantes.ISUSAEJBREMOTE || Constantes.ISUSAEJBHOME) {
				detallepedidosLocal=this.detallepedidos;
			}
			//ARCHITECTURE
		
			for(DetallePedido detallepedidoLocal:detallepedidosLocal) {
				if(this.permiteMantenimiento(detallepedidoLocal) && detallepedidoLocal.getIsSelected()) {
					tiene=true;
					break;
				}
			}
		} catch(Exception e) {
			throw e;
		}
		
		return tiene;
	}			
	
