	public int getIndiceNuevoDetallePedido() throws Exception {
		int iIndice=0;
		Boolean existe=false;
		
		//ARCHITECTURE
		if(Constantes.ISUSAEJBLOGICLAYER) {
			for(DetallePedido detallepedidoAux:this.detallepedidoLogic.getDetallePedidos()) {
				if(detallepedidoAux.getId().equals(this.iIdNuevoDetallePedido)) {
					existe=true;
					break;
				}
				
				iIndice++;
			}
		} else if(Constantes.ISUSAEJBREMOTE || Constantes.ISUSAEJBHOME) {
			for(DetallePedido detallepedidoAux:this.detallepedidos) {
				if(detallepedidoAux.getId().equals(this.iIdNuevoDetallePedido)) {
					existe=true;
					break;
				}
				
				iIndice++;
			}
		}
		//ARCHITECTURE
		
		if(!existe) {
			//SI NO EXISTE TOMA LA ULTIMA FILA
			iIndice=iIndice-1;
		}
		
		return iIndice;
	}
	
