	public void cancelarNuevosDetallePedido(Boolean esParaCancelar) throws Exception {
		detallepedidosAux=new ArrayList<DetallePedido>();
		detallepedidoAux=new DetallePedido();
		
		if(!this.detallepedidoSessionBean.getEsGuardarRelacionado()) {
			if(Constantes.ISUSAEJBLOGICLAYER) {
				for(DetallePedido detallepedidoAux:this.detallepedidoLogic.getDetallePedidos()) {
					if(detallepedidoAux.getId()<0) {
						detallepedidosAux.add(detallepedidoAux);
					}		
				}
				this.iIdNuevoDetallePedido=0L;
				this.detallepedidoLogic.getDetallePedidos().removeAll(detallepedidosAux);
					
			} else if(Constantes.ISUSAEJBREMOTE||Constantes.ISUSAEJBHOME) {
				for(DetallePedido detallepedidoAux:this.detallepedidos) {
					if(detallepedidoAux.getId()<0) {
						detallepedidosAux.add(detallepedidoAux);
					}		
				}
				this.iIdNuevoDetallePedido=0L;
				this.detallepedidos.removeAll(detallepedidosAux);
			}	
		} else {			
			if(Constantes.ISUSAEJBLOGICLAYER) {
				if(esParaCancelar && this.isEsNuevoDetallePedido 
					&& this.detallepedidoLogic.getDetallePedidos().size()>0
					) {
					detallepedidoAux=this.detallepedidoLogic.getDetallePedidos().get(this.detallepedidoLogic.getDetallePedidos().size() - 1);
				
					if(detallepedidoAux.getId()<0) {
						this.detallepedidoLogic.getDetallePedidos().remove(detallepedidoAux);
					}
				}
			} else if(Constantes.ISUSAEJBREMOTE||Constantes.ISUSAEJBHOME) {
				if(esParaCancelar && this.isEsNuevoDetallePedido && this.detallepedidos.size()>0) {
					detallepedidoAux=this.detallepedidos.get(this.detallepedidos.size() - 1);
				
					if(detallepedidoAux.getId()<0) {
						this.detallepedidos.remove(detallepedidoAux);
					}
				}
			}	
		}
	}
	
