	public void crearFilaTotales() throws Exception {
		Boolean existe=false;
		
		existe=this.existeFilaTotales();
		
		if(!existe) {
			//SI NO ES UNO A UNO SE CREA FILA TOTALES
			this.detallepedidoAux=new DetallePedido();
			this.detallepedidoAux.setsType(Constantes2.S_TOTALES);
			this.detallepedidoAux.setIsNew(false);
			this.detallepedidoAux.setIsChanged(false);
			this.detallepedidoAux.setIsDeleted(false);
				
			if(Constantes.ISUSAEJBLOGICLAYER) {
				DetallePedidoConstantesFunciones.TotalizarValoresFilaDetallePedido(this.detallepedidoLogic.getDetallePedidos(),this.detallepedidoAux);
				
				this.detallepedidoLogic.getDetallePedidos().add(this.detallepedidoAux);
				
			} else if(Constantes.ISUSAEJBREMOTE || Constantes.ISUSAEJBHOME) {
				DetallePedidoConstantesFunciones.TotalizarValoresFilaDetallePedido(this.detallepedidos,this.detallepedidoAux);
				
				this.detallepedidos.add(this.detallepedidoAux);
			}
		}
	}
	
