	public void aumentarTamanioFilaNuevaTablaDetallePedido() throws Exception {		
		Dimension dimensionMinimum=this.jTableDatosDetallePedido.getMinimumSize();
		Dimension dimensionMaximum=this.jTableDatosDetallePedido.getMaximumSize();
		Dimension dimensionPreferred=this.jTableDatosDetallePedido.getPreferredSize();
	
		double iHeightConFilaNueva=dimensionPreferred.getHeight();
		
		iHeightConFilaNueva+=this.jTableDatosDetallePedido.getRowHeight();
		
		dimensionMinimum.setSize(dimensionMinimum.getWidth(),iHeightConFilaNueva);
		dimensionMaximum.setSize(dimensionMaximum.getWidth(),iHeightConFilaNueva);
		dimensionPreferred.setSize(dimensionPreferred.getWidth(),iHeightConFilaNueva);
		
		this.jTableDatosDetallePedido.setMinimumSize(dimensionMinimum);
		this.jTableDatosDetallePedido.setMaximumSize(dimensionMaximum);
		this.jTableDatosDetallePedido.setPreferredSize(dimensionPreferred);	
	}
	
