	public void jButtonIdActionPerformed(ActionEvent evt,int rowIndex,Boolean esRelaciones,Boolean esEliminar) { 
		try {
			if(!esEliminar) {
				this.estaModoSeleccionar=true;
				
				//this.isEsNuevoDetallePedido=false;
					
				DetallePedidoBeanSwingJInternalFrameAdditional.procesarEventosVista(this.parametroGeneralUsuario,this.moduloActual,this.opcionActual,this.usuarioActual,this,FuncionTipo.BEFORE,ControlTipo.FORM,EventoTipo.LOAD,EventoSubTipo.SELECTED,"FORM",this.detallepedido,new Object(),this.detallepedidoParameterGeneral,this.detallepedidoReturnGeneral);
			
				if(this.detallepedidoSessionBean.getConGuardarRelaciones()) {
					this.dStart=(double)System.currentTimeMillis();
				}
				
				if(this.jInternalFrameDetalleFormDetallePedido==null) {
					this.inicializarFormDetalle();
				}
				
				this.inicializarInvalidValues();
				
				int intSelectedRow = 0;
				
				if(rowIndex>=0) {
					intSelectedRow=rowIndex;
					this.jTableDatosDetallePedido.getSelectionModel().setSelectionInterval(intSelectedRow, intSelectedRow);
				} else {	
					intSelectedRow=this.jTableDatosDetallePedido.getSelectedRow();	       
				}
				
				//ARCHITECTURE
				if(Constantes.ISUSAEJBLOGICLAYER) {
					this.detallepedido =(DetallePedido) this.detallepedidoLogic.getDetallePedidos().toArray()[this.jTableDatosDetallePedido.convertRowIndexToModel(intSelectedRow)];
				} else if(Constantes.ISUSAEJBREMOTE || Constantes.ISUSAEJBHOME ) {
					this.detallepedido =(DetallePedido) this.detallepedidos.toArray()[this.jTableDatosDetallePedido.convertRowIndexToModel(intSelectedRow)];
				}
				//ARCHITECTURE
				
				//PUEDE SER PARA DUPLICADO O NUEVO TABLA
				
				if(this.detallepedido.getsType().equals("DUPLICADO")
				   || this.detallepedido.getsType().equals("NUEVO_GUARDAR_CAMBIOS")) {
					
					this.isEsNuevoDetallePedido=true;
				
				} else {
					this.isEsNuevoDetallePedido=false;	
				}
				
				//CONTROL VERSION ANTERIOR
				/*
				if(!this.detallepedidoSessionBean.getEsGuardarRelacionado()) {
					if(this.detallepedido.getId()>=0 && !this.detallepedido.getIsNew()) {						
						this.isEsNuevoDetallePedido=false;
						
					} else {
						this.isEsNuevoDetallePedido=true;
					}
					
				} else {
					//CONTROLAR PARA RELACIONADO
				}
				*/
				
				//ESTABLECE SI ES RELACIONADO O NO 
				this.habilitarDeshabilitarTipoMantenimientoDetallePedido(esRelaciones);						
				
				this.seleccionarDetallePedido(evt,null,rowIndex);
				
				//SELECCIONA ACTUAL PERO AUN NO SE HA INGRESADO AL SISTEMA
				//SE DESHABILITA POR GUARDAR CAMBIOS
				/*
				if(this.detallepedido.getId()<0) {
					this.isEsNuevoDetallePedido=true;
				}
				*/
				
				if(!this.esParaBusquedaForeignKey) {
					this.modificarDetallePedido(evt,rowIndex,esRelaciones);
				} else {
					this.seleccionarDetallePedido(evt,rowIndex);
				}	
				
				if(this.detallepedidoSessionBean.getConGuardarRelaciones()) {
					this.dEnd=(double)System.currentTimeMillis();					
					this.dDif=this.dEnd - this.dStart;
					
					if(Constantes.ISDEVELOPING) {
						System.out.println("Tiempo(ms) Seleccion DetallePedido: " + this.dDif); 
					}
				}								
				
				DetallePedidoBeanSwingJInternalFrameAdditional.procesarEventosVista(this.parametroGeneralUsuario,this.moduloActual,this.opcionActual,this.usuarioActual,this,FuncionTipo.AFTER,ControlTipo.FORM,EventoTipo.LOAD,EventoSubTipo.SELECTED,"FORM",this.detallepedido,new Object(),this.detallepedidoParameterGeneral,this.detallepedidoReturnGeneral);
				
			} else {
				this.estaModoEliminarGuardarCambios=true;
				
				this.seleccionarDetallePedido(evt,null,rowIndex);
				
				if(this.permiteMantenimiento(this.detallepedido)) {
					if(this.detallepedido.getId()>0) {
						this.detallepedido.setIsDeleted(true);
						
						this.detallepedidosEliminados.add(this.detallepedido);
					}
					
					if(Constantes.ISUSAEJBLOGICLAYER) {				
						this.detallepedidoLogic.getDetallePedidos().remove(this.detallepedido);
					} else if(Constantes.ISUSAEJBREMOTE||Constantes.ISUSAEJBHOME) {
						this.detallepedidos.remove(this.detallepedido);				
					}
					
					
					((DetallePedidoModel) this.jTableDatosDetallePedido.getModel()).fireTableRowsDeleted(rowIndex,rowIndex);
					
					this.actualizarFilaTotales();
					
					this.inicializarActualizarBindingTablaDetallePedido(false);					
				}								
			}
			
		} catch(Exception e) {
			FuncionesSwing.manageException2(this, e,logger,DetallePedidoConstantesFunciones.CLASSNAME);
			
		} finally {
			this.estaModoSeleccionar=false;				
			this.estaModoEliminarGuardarCambios=false;
		}
	}
	
	
