	public void actualizarInformacion(String sTipo,Boolean conParametroObjeto,DetallePedido detallepedidoLocal) throws Exception {
		
		if(!conParametroObjeto) {
			if(!this.getEsControlTabla()) {
				detallepedidoLocal=this.detallepedido;
			} else {
				detallepedidoLocal=this.detallepedidoAnterior;
			}
		}
		
		if(this.permiteMantenimiento(detallepedidoLocal)) {
			if(sTipo.equals("EVENTO_CONTROL")) { // || sTipo.equals("EVENTO_NUEVO")
				if(!this.esControlTabla) {
					this.setVariablesFormularioToObjetoActualTodoDetallePedido(detallepedidoLocal,true);
					
					if(detallepedidoSessionBean.getConGuardarRelaciones()) {
						this.actualizarRelaciones(detallepedidoLocal);
					}
				}
			
			} else if(sTipo.equals("INFO_PADRE")) {
				
				if(this.detallepedidoSessionBean.getEsGuardarRelacionado()) {
					this.actualizarRelacionFkPadreActual(detallepedidoLocal);
				}
			}
		}
	}
	
