	public Boolean existeCambioValor(ControlTipo controlTipo,String sTipo) throws Exception {
		Boolean existeCambio=true;
		
		try {
			DetallePedido detallepedidoLocal=null;
			
			if(!this.getEsControlTabla()) {
				detallepedidoLocal=this.detallepedido;
			} else {
				detallepedidoLocal=this.detallepedidoAnterior;
			}
			
			if(controlTipo.equals(ControlTipo.TEXTBOX)) {
				


				if(sTipo.equals("cantidadDetallePedido")) {
					if(detallepedidoLocal.getcantidad()==Integer.parseInt(this.jInternalFrameDetalleFormDetallePedido.jTextFieldcantidadDetallePedido.getText())){existeCambio=false;};
				}
			}
		
		} catch(Exception e) {
  			FuncionesSwing.manageException2(this, e,logger,DetallePedidoConstantesFunciones.CLASSNAME);
  		}
		
		return existeCambio;
	}
	
