	public ArrayList<DetallePedido> getDetallePedidosSeleccionados(Boolean conSeleccionarTodosAutomatico) throws Exception {
		ArrayList<DetallePedido> detallepedidosSeleccionados=new ArrayList<DetallePedido>();
		Boolean existe=false;
		
		if(!this.esParaAccionDesdeFormularioDetallePedido) {
			if(Constantes.ISUSAEJBLOGICLAYER) {	
				
				for(DetallePedido detallepedidoAux:detallepedidoLogic.getDetallePedidos()) {
					if(detallepedidoAux.getIsSelected()) {
						detallepedidosSeleccionados.add(detallepedidoAux);				
					}
				}
				
			} else if(Constantes.ISUSAEJBREMOTE||Constantes.ISUSAEJBHOME) {
				for(DetallePedido detallepedidoAux:this.detallepedidos) {
					if(detallepedidoAux.getIsSelected()) {
						detallepedidosSeleccionados.add(detallepedidoAux);				
					}
				}
			}
			
			if(detallepedidosSeleccionados.size()>0) {
				existe=true;
			}
			
			//SI NO ESTA NINGUNO SELECCIONADO SE SELECCIONA TODOS
			if(!existe) {
				if(conSeleccionarTodosAutomatico) {
					if(Constantes.ISUSAEJBLOGICLAYER) {				
						detallepedidosSeleccionados.addAll(this.detallepedidoLogic.getDetallePedidos());
					} else if(Constantes.ISUSAEJBREMOTE||Constantes.ISUSAEJBHOME) {
						detallepedidosSeleccionados.addAll(this.detallepedidos);				
					}
				}
			}
		} else {
			detallepedidosSeleccionados.add(this.detallepedido);
		}
		
		return detallepedidosSeleccionados;
	}
	
