	public void jButtonCopiarOrdenDetaProduActionPerformed(ActionEvent evt) throws Exception {   
		try {
			
			Boolean conSeleccionarFilaTabla=false;
			Integer iNumRowsSeleccionados=0;
			int[] intSelectedRows =null;
			int intSelectedRow =0;
			
			this.estaModoCopiar=true;
			
			ArrayList<OrdenDetaProdu> ordendetaprodusSeleccionados=new ArrayList<OrdenDetaProdu>();									
		
			OrdenDetaProdu ordendetaproduOrigen=new OrdenDetaProdu();
			OrdenDetaProdu ordendetaproduDestino=new OrdenDetaProdu();
				
			ordendetaprodusSeleccionados=this.getOrdenDetaProdusSeleccionados(false);
			
			if(conSeleccionarFilaTabla) {
				iNumRowsSeleccionados=this.jTableDatosOrdenDetaProdu.getSelectedRows().length;	
			}
			
			if(iNumRowsSeleccionados.equals(2) || ordendetaprodusSeleccionados.size()==2) {
				if(conSeleccionarFilaTabla) {
					intSelectedRows =this.jTableDatosOrdenDetaProdu.getSelectedRows();
					intSelectedRow = intSelectedRows[0];	
									
					//ARCHITECTURE
					if(Constantes.ISUSAEJBLOGICLAYER) {
						ordendetaproduOrigen =(OrdenDetaProdu) this.ordendetaproduLogic.getOrdenDetaProdus().toArray()[this.jTableDatosOrdenDetaProdu.convertRowIndexToModel(intSelectedRow)];
					} else if(Constantes.ISUSAEJBREMOTE || Constantes.ISUSAEJBHOME) {
						ordendetaproduOrigen =(OrdenDetaProdu) this.ordendetaprodus.toArray()[this.jTableDatosOrdenDetaProdu.convertRowIndexToModel(intSelectedRow)];
					}
					//ARCHITECTURE
					
					intSelectedRow = intSelectedRows[1];
					
					//ARCHITECTURE
					if(Constantes.ISUSAEJBLOGICLAYER) {
						ordendetaproduDestino =(OrdenDetaProdu) this.ordendetaproduLogic.getOrdenDetaProdus().toArray()[this.jTableDatosOrdenDetaProdu.convertRowIndexToModel(intSelectedRow)];
					} else if(Constantes.ISUSAEJBREMOTE || Constantes.ISUSAEJBHOME) {
						ordendetaproduDestino =(OrdenDetaProdu) this.ordendetaprodus.toArray()[this.jTableDatosOrdenDetaProdu.convertRowIndexToModel(intSelectedRow)];
					}
					//ARCHITECTURE
				}
				
				ordendetaproduOrigen =ordendetaprodusSeleccionados.get(0);
				ordendetaproduDestino =ordendetaprodusSeleccionados.get(1);
				
				this.setCopiarVariablesObjetosOrdenDetaProdu(ordendetaproduOrigen,ordendetaproduDestino,true,false);
				
				ordendetaproduDestino.setsType("DUPLICADO");
				
				//ARCHITECTURE
				if(Constantes.ISUSAEJBLOGICLAYER) {
					actualizarLista(ordendetaproduDestino,ordendetaproduLogic.getOrdenDetaProdus());					
				} else if(Constantes.ISUSAEJBREMOTE||Constantes.ISUSAEJBHOME) {
					actualizarLista(ordendetaproduDestino,ordendetaprodus);
				}
				//ARCHITECTURE
				
				this.inicializarActualizarBindingTablaOrdenDetaProdu(false);
				
				//this.jTableDatosOrdenDetaProdu.setRowSelectionInterval(this.getIndiceNuevoOrdenDetaProdu(), this.getIndiceNuevoOrdenDetaProdu());
				
				int iLastRow =  this.jTableDatosOrdenDetaProdu.getRowCount () - 1;
				Rectangle rectangle = this.jTableDatosOrdenDetaProdu.getCellRect(iLastRow, 0, true);
				
				this.jTableDatosOrdenDetaProdu.scrollRectToVisible(rectangle);
				
				//FILA TOTALES
				if(this.conTotales) {
					//this.crearFilaTotales();
					
					this.inicializarActualizarBindingTablaOrdenDetaProdu(false);
				}
			} else {
				throw new Exception("DEBEN ESTAR SELECCIONADOS 2 REGISTROS");
			}
		} catch(Exception e) {
			FuncionesSwing.manageException(this, e,logger,OrdenDetaProduConstantesFunciones.CLASSNAME);
		
		}  finally {
			this.estaModoCopiar=false;
		}
	}
	
