	public void aumentarTamanioFilaNuevaTablaOrdenDetaProdu() throws Exception {		
		Dimension dimensionMinimum=this.jTableDatosOrdenDetaProdu.getMinimumSize();
		Dimension dimensionMaximum=this.jTableDatosOrdenDetaProdu.getMaximumSize();
		Dimension dimensionPreferred=this.jTableDatosOrdenDetaProdu.getPreferredSize();
	
		double iHeightConFilaNueva=dimensionPreferred.getHeight();
		
		iHeightConFilaNueva+=this.jTableDatosOrdenDetaProdu.getRowHeight();
		
		dimensionMinimum.setSize(dimensionMinimum.getWidth(),iHeightConFilaNueva);
		dimensionMaximum.setSize(dimensionMaximum.getWidth(),iHeightConFilaNueva);
		dimensionPreferred.setSize(dimensionPreferred.getWidth(),iHeightConFilaNueva);
		
		this.jTableDatosOrdenDetaProdu.setMinimumSize(dimensionMinimum);
		this.jTableDatosOrdenDetaProdu.setMaximumSize(dimensionMaximum);
		this.jTableDatosOrdenDetaProdu.setPreferredSize(dimensionPreferred);	
	}
	
