	public void jButtonIdActionPerformed(ActionEvent evt,int rowIndex,Boolean esRelaciones,Boolean esEliminar) { 
		try {
			if(!esEliminar) {
				this.estaModoSeleccionar=true;
				
				//this.isEsNuevoOrdenDetaProdu=false;
					
				OrdenDetaProduBeanSwingJInternalFrameAdditional.procesarEventosVista(this.parametroGeneralUsuario,this.moduloActual,this.opcionActual,this.usuarioActual,this,FuncionTipo.BEFORE,ControlTipo.FORM,EventoTipo.LOAD,EventoSubTipo.SELECTED,"FORM",this.ordendetaprodu,new Object(),this.ordendetaproduParameterGeneral,this.ordendetaproduReturnGeneral);
			
				if(this.ordendetaproduSessionBean.getConGuardarRelaciones()) {
					this.dStart=(double)System.currentTimeMillis();
				}
				
				if(this.jInternalFrameDetalleFormOrdenDetaProdu==null) {
					this.inicializarFormDetalle();
				}
				
				this.inicializarInvalidValues();
				
				int intSelectedRow = 0;
				
				if(rowIndex>=0) {
					intSelectedRow=rowIndex;
					this.jTableDatosOrdenDetaProdu.getSelectionModel().setSelectionInterval(intSelectedRow, intSelectedRow);
				} else {	
					intSelectedRow=this.jTableDatosOrdenDetaProdu.getSelectedRow();	       
				}
				
				//ARCHITECTURE
				if(Constantes.ISUSAEJBLOGICLAYER) {
					this.ordendetaprodu =(OrdenDetaProdu) this.ordendetaproduLogic.getOrdenDetaProdus().toArray()[this.jTableDatosOrdenDetaProdu.convertRowIndexToModel(intSelectedRow)];
				} else if(Constantes.ISUSAEJBREMOTE || Constantes.ISUSAEJBHOME ) {
					this.ordendetaprodu =(OrdenDetaProdu) this.ordendetaprodus.toArray()[this.jTableDatosOrdenDetaProdu.convertRowIndexToModel(intSelectedRow)];
				}
				//ARCHITECTURE
				
				//PUEDE SER PARA DUPLICADO O NUEVO TABLA
				
				if(this.ordendetaprodu.getsType().equals("DUPLICADO")
				   || this.ordendetaprodu.getsType().equals("NUEVO_GUARDAR_CAMBIOS")) {
					
					this.isEsNuevoOrdenDetaProdu=true;
				
				} else {
					this.isEsNuevoOrdenDetaProdu=false;	
				}
				
				//CONTROL VERSION ANTERIOR
				/*
				if(!this.ordendetaproduSessionBean.getEsGuardarRelacionado()) {
					if(this.ordendetaprodu.getId()>=0 && !this.ordendetaprodu.getIsNew()) {						
						this.isEsNuevoOrdenDetaProdu=false;
						
					} else {
						this.isEsNuevoOrdenDetaProdu=true;
					}
					
				} else {
					//CONTROLAR PARA RELACIONADO
				}
				*/
				
				//ESTABLECE SI ES RELACIONADO O NO 
				this.habilitarDeshabilitarTipoMantenimientoOrdenDetaProdu(esRelaciones);						
				
				this.seleccionarOrdenDetaProdu(evt,null,rowIndex);
				
				//SELECCIONA ACTUAL PERO AUN NO SE HA INGRESADO AL SISTEMA
				//SE DESHABILITA POR GUARDAR CAMBIOS
				/*
				if(this.ordendetaprodu.getId()<0) {
					this.isEsNuevoOrdenDetaProdu=true;
				}
				*/
				
				if(!this.esParaBusquedaForeignKey) {
					this.modificarOrdenDetaProdu(evt,rowIndex,esRelaciones);
				} else {
					this.seleccionarOrdenDetaProdu(evt,rowIndex);
				}	
				
				if(this.ordendetaproduSessionBean.getConGuardarRelaciones()) {
					this.dEnd=(double)System.currentTimeMillis();					
					this.dDif=this.dEnd - this.dStart;
					
					if(Constantes.ISDEVELOPING) {
						System.out.println("Tiempo(ms) Seleccion OrdenDetaProdu: " + this.dDif); 
					}
				}								
				
				OrdenDetaProduBeanSwingJInternalFrameAdditional.procesarEventosVista(this.parametroGeneralUsuario,this.moduloActual,this.opcionActual,this.usuarioActual,this,FuncionTipo.AFTER,ControlTipo.FORM,EventoTipo.LOAD,EventoSubTipo.SELECTED,"FORM",this.ordendetaprodu,new Object(),this.ordendetaproduParameterGeneral,this.ordendetaproduReturnGeneral);
				
			} else {
				this.estaModoEliminarGuardarCambios=true;
				
				this.seleccionarOrdenDetaProdu(evt,null,rowIndex);
				
				if(this.permiteMantenimiento(this.ordendetaprodu)) {
					if(this.ordendetaprodu.getId()>0) {
						this.ordendetaprodu.setIsDeleted(true);
						
						this.ordendetaprodusEliminados.add(this.ordendetaprodu);
					}
					
					if(Constantes.ISUSAEJBLOGICLAYER) {				
						this.ordendetaproduLogic.getOrdenDetaProdus().remove(this.ordendetaprodu);
					} else if(Constantes.ISUSAEJBREMOTE||Constantes.ISUSAEJBHOME) {
						this.ordendetaprodus.remove(this.ordendetaprodu);				
					}
					
					
					((OrdenDetaProduModel) this.jTableDatosOrdenDetaProdu.getModel()).fireTableRowsDeleted(rowIndex,rowIndex);
					
					this.actualizarFilaTotales();
					
					this.inicializarActualizarBindingTablaOrdenDetaProdu(false);					
				}								
			}
			
		} catch(Exception e) {
			FuncionesSwing.manageException2(this, e,logger,OrdenDetaProduConstantesFunciones.CLASSNAME);
			
		} finally {
			this.estaModoSeleccionar=false;				
			this.estaModoEliminarGuardarCambios=false;
		}
	}
	
	
