	public void actualizarInformacion(String sTipo,Boolean conParametroObjeto,OrdenDetaProdu ordendetaproduLocal) throws Exception {
		
		if(!conParametroObjeto) {
			if(!this.getEsControlTabla()) {
				ordendetaproduLocal=this.ordendetaprodu;
			} else {
				ordendetaproduLocal=this.ordendetaproduAnterior;
			}
		}
		
		if(this.permiteMantenimiento(ordendetaproduLocal)) {
			if(sTipo.equals("EVENTO_CONTROL")) { // || sTipo.equals("EVENTO_NUEVO")
				if(!this.esControlTabla) {
					this.setVariablesFormularioToObjetoActualTodoOrdenDetaProdu(ordendetaproduLocal,true);
					
					if(ordendetaproduSessionBean.getConGuardarRelaciones()) {
						this.actualizarRelaciones(ordendetaproduLocal);
					}
				}
			
			} else if(sTipo.equals("INFO_PADRE")) {
				
				if(this.ordendetaproduSessionBean.getEsGuardarRelacionado()) {
					this.actualizarRelacionFkPadreActual(ordendetaproduLocal);
				}
			}
		}
	}
	
