	public ArrayList<OrdenDetaProdu> getOrdenDetaProdusSeleccionados(Boolean conSeleccionarTodosAutomatico) throws Exception {
		ArrayList<OrdenDetaProdu> ordendetaprodusSeleccionados=new ArrayList<OrdenDetaProdu>();
		Boolean existe=false;
		
		if(!this.esParaAccionDesdeFormularioOrdenDetaProdu) {
			if(Constantes.ISUSAEJBLOGICLAYER) {	
				
				for(OrdenDetaProdu ordendetaproduAux:ordendetaproduLogic.getOrdenDetaProdus()) {
					if(ordendetaproduAux.getIsSelected()) {
						ordendetaprodusSeleccionados.add(ordendetaproduAux);				
					}
				}
				
			} else if(Constantes.ISUSAEJBREMOTE||Constantes.ISUSAEJBHOME) {
				for(OrdenDetaProdu ordendetaproduAux:this.ordendetaprodus) {
					if(ordendetaproduAux.getIsSelected()) {
						ordendetaprodusSeleccionados.add(ordendetaproduAux);				
					}
				}
			}
			
			if(ordendetaprodusSeleccionados.size()>0) {
				existe=true;
			}
			
			//SI NO ESTA NINGUNO SELECCIONADO SE SELECCIONA TODOS
			if(!existe) {
				if(conSeleccionarTodosAutomatico) {
					if(Constantes.ISUSAEJBLOGICLAYER) {				
						ordendetaprodusSeleccionados.addAll(this.ordendetaproduLogic.getOrdenDetaProdus());
					} else if(Constantes.ISUSAEJBREMOTE||Constantes.ISUSAEJBHOME) {
						ordendetaprodusSeleccionados.addAll(this.ordendetaprodus);				
					}
				}
			}
		} else {
			ordendetaprodusSeleccionados.add(this.ordendetaprodu);
		}
		
		return ordendetaprodusSeleccionados;
	}
	
