	public void jButtonNuevoUsuarioActionPerformed(ActionEvent evt,Boolean esRelaciones) throws Exception {   
		try {
			EventoGlobalTipo eventoGlobalTipo=EventoGlobalTipo.FORM_RECARGAR;
			String sTipo="NUEVO_NORMAL";
			
			this.estaModoNuevo=true;
			
			if(this.usuarioSessionBean.getConGuardarRelaciones()) {
				this.dStart=(double)System.currentTimeMillis();
			}
				
			//if(this.esUsoDesdeHijo) {
			//	eventoGlobalTipo=EventoGlobalTipo.FORM_HIJO_ACTUALIZAR;
			//}
											
			if(this.jInternalFrameDetalleFormUsuario==null) { //if(!this.conCargarFormDetalle) {
				this.inicializarFormDetalle();
			}			
				
			UsuarioBeanSwingJInternalFrameAdditional.procesarEventosVista(this.parametroGeneralUsuario,this.moduloActual,this.opcionActual,this.usuarioActual,this,FuncionTipo.BEFORE,ControlTipo.FORM,EventoTipo.LOAD,EventoSubTipo.NEW,"FORM",this.usuario,new Object(),this.usuarioParameterGeneral,this.usuarioReturnGeneral);
			
			
			if(jTableDatosUsuario.getRowCount()>=1) {
				jTableDatosUsuario.removeRowSelectionInterval(0, jTableDatosUsuario.getRowCount()-1);						
			}
			
			this.isEsNuevoUsuario=true;
			
			//ESTABLECE SI ES RELACIONADO O NO 
			this.habilitarDeshabilitarTipoMantenimientoUsuario(esRelaciones);
			
			this.nuevoPreparar(false); 
			this.habilitarDeshabilitarControlesUsuario(true);			
			//this.usuario=new Usuario();
			//this.usuario.setIsChanged(true);
			
			//NO FUNCIONA BINDINGS
			this.inicializarActualizarBindingBotonesUsuario(false) ;						
			 
			//SI ES MANUAL
			//this.inicializarActualizarBindingBotonesManualUsuario() ;
			
			if(UsuarioJInternalFrame.CON_DATOS_FRAME) {
				this.abrirFrameDetalleUsuario(esRelaciones);
			}
					
			//Se Duplica, sin sentido
			//this.actualizarInformacion("EVENTO_NUEVO",false,this.usuario);	
			this.actualizarInformacion("INFO_PADRE",false,this.usuario);				
			
			UsuarioBeanSwingJInternalFrameAdditional.procesarEventosVista(this.parametroGeneralUsuario,this.moduloActual,this.opcionActual,this.usuarioActual,this,FuncionTipo.AFTER,ControlTipo.FORM,EventoTipo.LOAD,EventoSubTipo.NEW,"FORM",this.usuario,new Object(),this.usuarioParameterGeneral,this.usuarioReturnGeneral);
			
			if(this.usuarioSessionBean.getConGuardarRelaciones()) {
				this.dEnd=(double)System.currentTimeMillis();					
				this.dDif=this.dEnd - this.dStart;
					
				if(Constantes.ISDEVELOPING) {
					System.out.println("Tiempo(ms) Nuevo Preparar Usuario: " + this.dDif); 
				}
			}
			
			//false para que pueda generar eventos
			this.estaModoNuevo=false;
							
			//Con this.estaModoNuevo=false;, se permite actualizar y usar eventos control al mismo tiempo (FuncionTipo.LAST)			
			UsuarioBeanSwingJInternalFrameAdditional.procesarEventosVista(this.parametroGeneralUsuario,this.moduloActual,this.opcionActual,this.usuarioActual,this,FuncionTipo.LAST,ControlTipo.FORM,EventoTipo.CLIC,EventoSubTipo.NEW,sTipo,this.usuario,new Object(),this.usuarioParameterGeneral,this.usuarioReturnGeneral);
			
		} catch(Exception e) {									
			FuncionesSwing.manageException(this, e,logger,UsuarioConstantesFunciones.CLASSNAME);
			
		} finally {
			this.estaModoNuevo=false;			
		}
	}
	
	public void jButtonDuplicarUsuarioActionPerformed(ActionEvent evt,Boolean esRelaciones) throws Exception {   
		try {
			Boolean soloDuplicarUno=false;
			Boolean conSeleccionarFilaTabla=false;
			
			this.estaModoNuevo=true;
			this.estaModoDuplicar=true;
			
			ArrayList<Usuario> usuariosSeleccionados=new ArrayList<Usuario>();
			int intSelectedRow =-1;
			Integer iNumRowsSeleccionados=0;
			int[] arrNumRowsSeleccionados=null;
			
			//NO SE TOMA EN CUENTA, SI LOS SELECCIONADOS
			if(conSeleccionarFilaTabla) {
				arrNumRowsSeleccionados=this.jTableDatosUsuario.getSelectedRows();
				iNumRowsSeleccionados=this.jTableDatosUsuario.getSelectedRows().length;			
			}
			
			usuariosSeleccionados=this.getUsuariosSeleccionados(false);
				
			if((soloDuplicarUno && iNumRowsSeleccionados.equals(1)) || !soloDuplicarUno) {
				//LO HACE NUEVOPREPARAR
				//this.iIdNuevoUsuario--;			
				//Usuario usuarioAux= new Usuario();			
				//usuarioAux.setId(this.iIdNuevoUsuario);																
				
				//NO SE TOMA EN CUENTA, SI LOS SELECCIONADOS
				//Usuario usuarioOrigen=new Usuario();
				//for(Integer iNumRowSeleccionado:arrNumRowsSeleccionados) {				
				
				for(Usuario usuarioOrigen : usuariosSeleccionados) {
					if(conSeleccionarFilaTabla) {
						if(!soloDuplicarUno) {
							//NO SE TOMA EN CUENTA, SI LOS SELECCIONADOS
							//intSelectedRow =iNumRowSeleccionado;
						} else {
							intSelectedRow = this.jTableDatosUsuario.getSelectedRow();
						}
						
						//ARCHITECTURE
						if(Constantes.ISUSAEJBLOGICLAYER) {
							usuarioOrigen =(Usuario) this.usuarioLogic.getUsuarios().toArray()[this.jTableDatosUsuario.convertRowIndexToModel(intSelectedRow)];
						} else if(Constantes.ISUSAEJBREMOTE || Constantes.ISUSAEJBHOME) {
							usuarioOrigen =(Usuario) this.usuarios.toArray()[this.jTableDatosUsuario.convertRowIndexToModel(intSelectedRow)];
						}
					}
					
					this.aumentarTamanioFilaNuevaTablaUsuario();
					
					if(this.conTotales) {
						this.quitarFilaTotales();
					}
					
					this.nuevoPreparar(true);
					
					this.usuario.setsType("DUPLICADO");
					
					this.setCopiarVariablesObjetosUsuario(usuarioOrigen,this.usuario,true,true);
					
					this.setVariablesFormularioToObjetoActualForeignKeysUsuario(this.usuario);
					
					//LO HACE NUEVOPREPARAR
					/*
					if(Constantes.ISUSAEJBLOGICLAYER) {				
						this.usuarioLogic.getUsuarios().add(this.usuarioAux);
					} else if(Constantes.ISUSAEJBREMOTE||Constantes.ISUSAEJBHOME) {
						this.usuarios.add(this.usuarioAux);				
					}
					*/
				}
				
				this.inicializarActualizarBindingTablaUsuario(false);
				
				this.jTableDatosUsuario.setRowSelectionInterval(this.getIndiceNuevoUsuario(), this.getIndiceNuevoUsuario());
				
				int iLastRow =  this.jTableDatosUsuario.getRowCount () - 1;
				Rectangle rectangle = this.jTableDatosUsuario.getCellRect(iLastRow, 0, true);
				
				this.jTableDatosUsuario.scrollRectToVisible(rectangle);
				
				//FILA TOTALES
				if(this.conTotales) {
					this.crearFilaTotales();
					
					this.inicializarActualizarBindingTablaUsuario(false);
				}
			} else {
				throw new Exception("DEBE ESTAR SELECCIONADO 1 REGISTRO");
			}
			
		} catch(Exception e) {			
			FuncionesSwing.manageException(this, e,logger,UsuarioConstantesFunciones.CLASSNAME);
		
		} finally {
			this.estaModoNuevo=false;
			this.estaModoDuplicar=false;
		}
	}
	
	public void jButtonCopiarUsuarioActionPerformed(ActionEvent evt) throws Exception {   
		try {
			
			Boolean conSeleccionarFilaTabla=false;
			Integer iNumRowsSeleccionados=0;
			int[] intSelectedRows =null;
			int intSelectedRow =0;
			
			this.estaModoCopiar=true;
			
			ArrayList<Usuario> usuariosSeleccionados=new ArrayList<Usuario>();									
		
			Usuario usuarioOrigen=new Usuario();
			Usuario usuarioDestino=new Usuario();
				
			usuariosSeleccionados=this.getUsuariosSeleccionados(false);
			
			if(conSeleccionarFilaTabla) {
				iNumRowsSeleccionados=this.jTableDatosUsuario.getSelectedRows().length;	
			}
			
			if(iNumRowsSeleccionados.equals(2) || usuariosSeleccionados.size()==2) {
				if(conSeleccionarFilaTabla) {
					intSelectedRows =this.jTableDatosUsuario.getSelectedRows();
					intSelectedRow = intSelectedRows[0];	
									
					//ARCHITECTURE
					if(Constantes.ISUSAEJBLOGICLAYER) {
						usuarioOrigen =(Usuario) this.usuarioLogic.getUsuarios().toArray()[this.jTableDatosUsuario.convertRowIndexToModel(intSelectedRow)];
					} else if(Constantes.ISUSAEJBREMOTE || Constantes.ISUSAEJBHOME) {
						usuarioOrigen =(Usuario) this.usuarios.toArray()[this.jTableDatosUsuario.convertRowIndexToModel(intSelectedRow)];
					}
					//ARCHITECTURE
					
					intSelectedRow = intSelectedRows[1];
					
					//ARCHITECTURE
					if(Constantes.ISUSAEJBLOGICLAYER) {
						usuarioDestino =(Usuario) this.usuarioLogic.getUsuarios().toArray()[this.jTableDatosUsuario.convertRowIndexToModel(intSelectedRow)];
					} else if(Constantes.ISUSAEJBREMOTE || Constantes.ISUSAEJBHOME) {
						usuarioDestino =(Usuario) this.usuarios.toArray()[this.jTableDatosUsuario.convertRowIndexToModel(intSelectedRow)];
					}
					//ARCHITECTURE
				}
				
				usuarioOrigen =usuariosSeleccionados.get(0);
				usuarioDestino =usuariosSeleccionados.get(1);
				
				this.setCopiarVariablesObjetosUsuario(usuarioOrigen,usuarioDestino,true,false);
				
				usuarioDestino.setsType("DUPLICADO");
				
				//ARCHITECTURE
				if(Constantes.ISUSAEJBLOGICLAYER) {
					actualizarLista(usuarioDestino,usuarioLogic.getUsuarios());					
				} else if(Constantes.ISUSAEJBREMOTE||Constantes.ISUSAEJBHOME) {
					actualizarLista(usuarioDestino,usuarios);
				}
				//ARCHITECTURE
				
				this.inicializarActualizarBindingTablaUsuario(false);
				
				//this.jTableDatosUsuario.setRowSelectionInterval(this.getIndiceNuevoUsuario(), this.getIndiceNuevoUsuario());
				
				int iLastRow =  this.jTableDatosUsuario.getRowCount () - 1;
				Rectangle rectangle = this.jTableDatosUsuario.getCellRect(iLastRow, 0, true);
				
				this.jTableDatosUsuario.scrollRectToVisible(rectangle);
				
				//FILA TOTALES
				if(this.conTotales) {
					//this.crearFilaTotales();
					
					this.inicializarActualizarBindingTablaUsuario(false);
				}
			} else {
				throw new Exception("DEBEN ESTAR SELECCIONADOS 2 REGISTROS");
			}
		} catch(Exception e) {
			FuncionesSwing.manageException(this, e,logger,UsuarioConstantesFunciones.CLASSNAME);
		
		}  finally {
			this.estaModoCopiar=false;
		}
	}
	
