	public void aumentarTamanioFilaNuevaTablaUsuario() throws Exception {		
		Dimension dimensionMinimum=this.jTableDatosUsuario.getMinimumSize();
		Dimension dimensionMaximum=this.jTableDatosUsuario.getMaximumSize();
		Dimension dimensionPreferred=this.jTableDatosUsuario.getPreferredSize();
	
		double iHeightConFilaNueva=dimensionPreferred.getHeight();
		
		iHeightConFilaNueva+=this.jTableDatosUsuario.getRowHeight();
		
		dimensionMinimum.setSize(dimensionMinimum.getWidth(),iHeightConFilaNueva);
		dimensionMaximum.setSize(dimensionMaximum.getWidth(),iHeightConFilaNueva);
		dimensionPreferred.setSize(dimensionPreferred.getWidth(),iHeightConFilaNueva);
		
		this.jTableDatosUsuario.setMinimumSize(dimensionMinimum);
		this.jTableDatosUsuario.setMaximumSize(dimensionMaximum);
		this.jTableDatosUsuario.setPreferredSize(dimensionPreferred);	
	}
	
