	public void jButtonIdActionPerformed(ActionEvent evt,int rowIndex,Boolean esRelaciones,Boolean esEliminar) { 
		try {
			if(!esEliminar) {
				this.estaModoSeleccionar=true;
				
				//this.isEsNuevoUsuario=false;
					
				UsuarioBeanSwingJInternalFrameAdditional.procesarEventosVista(this.parametroGeneralUsuario,this.moduloActual,this.opcionActual,this.usuarioActual,this,FuncionTipo.BEFORE,ControlTipo.FORM,EventoTipo.LOAD,EventoSubTipo.SELECTED,"FORM",this.usuario,new Object(),this.usuarioParameterGeneral,this.usuarioReturnGeneral);
			
				if(this.usuarioSessionBean.getConGuardarRelaciones()) {
					this.dStart=(double)System.currentTimeMillis();
				}
				
				if(this.jInternalFrameDetalleFormUsuario==null) {
					this.inicializarFormDetalle();
				}
				
				this.inicializarInvalidValues();
				
				int intSelectedRow = 0;
				
				if(rowIndex>=0) {
					intSelectedRow=rowIndex;
					this.jTableDatosUsuario.getSelectionModel().setSelectionInterval(intSelectedRow, intSelectedRow);
				} else {	
					intSelectedRow=this.jTableDatosUsuario.getSelectedRow();	       
				}
				
				//ARCHITECTURE
				if(Constantes.ISUSAEJBLOGICLAYER) {
					this.usuario =(Usuario) this.usuarioLogic.getUsuarios().toArray()[this.jTableDatosUsuario.convertRowIndexToModel(intSelectedRow)];
				} else if(Constantes.ISUSAEJBREMOTE || Constantes.ISUSAEJBHOME ) {
					this.usuario =(Usuario) this.usuarios.toArray()[this.jTableDatosUsuario.convertRowIndexToModel(intSelectedRow)];
				}
				//ARCHITECTURE
				
				//PUEDE SER PARA DUPLICADO O NUEVO TABLA
				
				if(this.usuario.getsType().equals("DUPLICADO")
				   || this.usuario.getsType().equals("NUEVO_GUARDAR_CAMBIOS")) {
					
					this.isEsNuevoUsuario=true;
				
				} else {
					this.isEsNuevoUsuario=false;	
				}
				
				//CONTROL VERSION ANTERIOR
				/*
				if(!this.usuarioSessionBean.getEsGuardarRelacionado()) {
					if(this.usuario.getId()>=0 && !this.usuario.getIsNew()) {						
						this.isEsNuevoUsuario=false;
						
					} else {
						this.isEsNuevoUsuario=true;
					}
					
				} else {
					//CONTROLAR PARA RELACIONADO
				}
				*/
				
				//ESTABLECE SI ES RELACIONADO O NO 
				this.habilitarDeshabilitarTipoMantenimientoUsuario(esRelaciones);						
				
				this.seleccionarUsuario(evt,null,rowIndex);
				
				//SELECCIONA ACTUAL PERO AUN NO SE HA INGRESADO AL SISTEMA
				//SE DESHABILITA POR GUARDAR CAMBIOS
				/*
				if(this.usuario.getId()<0) {
					this.isEsNuevoUsuario=true;
				}
				*/
				
				if(!this.esParaBusquedaForeignKey) {
					this.modificarUsuario(evt,rowIndex,esRelaciones);
				} else {
					this.seleccionarUsuario(evt,rowIndex);
				}	
				
				if(this.usuarioSessionBean.getConGuardarRelaciones()) {
					this.dEnd=(double)System.currentTimeMillis();					
					this.dDif=this.dEnd - this.dStart;
					
					if(Constantes.ISDEVELOPING) {
						System.out.println("Tiempo(ms) Seleccion Usuario: " + this.dDif); 
					}
				}								
				
				UsuarioBeanSwingJInternalFrameAdditional.procesarEventosVista(this.parametroGeneralUsuario,this.moduloActual,this.opcionActual,this.usuarioActual,this,FuncionTipo.AFTER,ControlTipo.FORM,EventoTipo.LOAD,EventoSubTipo.SELECTED,"FORM",this.usuario,new Object(),this.usuarioParameterGeneral,this.usuarioReturnGeneral);
				
			} else {
				this.estaModoEliminarGuardarCambios=true;
				
				this.seleccionarUsuario(evt,null,rowIndex);
				
				if(this.permiteMantenimiento(this.usuario)) {
					if(this.usuario.getId()>0) {
						this.usuario.setIsDeleted(true);
						
						this.usuariosEliminados.add(this.usuario);
					}
					
					if(Constantes.ISUSAEJBLOGICLAYER) {				
						this.usuarioLogic.getUsuarios().remove(this.usuario);
					} else if(Constantes.ISUSAEJBREMOTE||Constantes.ISUSAEJBHOME) {
						this.usuarios.remove(this.usuario);				
					}
					
					
					((UsuarioModel) this.jTableDatosUsuario.getModel()).fireTableRowsDeleted(rowIndex,rowIndex);
					
					this.actualizarFilaTotales();
					
					this.inicializarActualizarBindingTablaUsuario(false);					
				}								
			}
			
		} catch(Exception e) {
			FuncionesSwing.manageException2(this, e,logger,UsuarioConstantesFunciones.CLASSNAME);
			
		} finally {
			this.estaModoSeleccionar=false;				
			this.estaModoEliminarGuardarCambios=false;
		}
	}
	
	
	public void seleccionarUsuario(ActionEvent evt,javax.swing.event.ListSelectionEvent evt2,int rowIndex) throws Exception { 
		try {
			//SI PUEDE SER NUEVO Y SELECCIONAR (PARA DUPLICAR Y NUEVO TABLA)
			//if(!this.isEsNuevoUsuario) {
			
			if(this.jInternalFrameDetalleFormUsuario==null) { //if(!this.conCargarFormDetalle) {
				this.inicializarFormDetalle();
			}
			
				int intSelectedRow = 0;
				
				if(rowIndex>=0) {
					intSelectedRow=rowIndex;
					this.jTableDatosUsuario.getSelectionModel().setSelectionInterval(intSelectedRow, intSelectedRow);
				} else {	
					intSelectedRow=this.jTableDatosUsuario.getSelectedRow();	       
				}
				
				//CUANDO SE RECARGA TABLA TAMBIEN SE SELECCIONA PERO CON -1 POR LO QUE SE NECESITA VALIDAR ANTES
				if(intSelectedRow<0) {
					return;
				}
				
				//ARCHITECTURE
				if(Constantes.ISUSAEJBLOGICLAYER) {
					this.usuario =(Usuario) this.usuarioLogic.getUsuarios().toArray()[this.jTableDatosUsuario.convertRowIndexToModel(intSelectedRow)];
				} else if(Constantes.ISUSAEJBREMOTE || Constantes.ISUSAEJBHOME ) {
					this.usuario =(Usuario) this.usuarios.toArray()[this.jTableDatosUsuario.convertRowIndexToModel(intSelectedRow)];
				}
				
				if(UsuarioJInternalFrame.ISBINDING_MANUAL_TABLA) {
					this.setVariablesObjetoActualToFormularioUsuario(this.usuario);
				}
				
				//ARCHITECTURE
				try {
					

					//Empresa
					if(!this.usuarioConstantesFunciones.cargarid_empresaUsuario || this.usuarioConstantesFunciones.event_dependid_empresaUsuario) {
						//this.cargarCombosEmpresasForeignKeyLista(" where id="+this.usuario.getid_empresa());
									//this.inicializarActualizarBindingUsuario(false,false);
						this.empresasForeignKey=new ArrayList<Empresa>();

						if(usuario.getEmpresa()!=null) {
							this.empresasForeignKey.add(usuario.getEmpresa());
						}

						this.addItemDefectoCombosForeignKeyEmpresa();
						this.cargarCombosFrameEmpresasForeignKey("Todos");
					}
					this.setActualEmpresaForeignKey(this.usuario.getid_empresa(),false,"Formulario");
				} catch(Exception e) {
					throw e;
				}
				
				this.actualizarEstadoCeldasBotonesUsuario("s", this.isGuardarCambiosEnLote, this.isEsMantenimientoRelacionado);
				
				//NO FUNCIONA BINDING PERO SE MANTIENE
				this.inicializarActualizarBindingBotonesUsuario(false) ;
				
				//SI ES MANUAL
				//this.inicializarActualizarBindingBotonesManualUsuario() ;
			//}
		} catch(Exception e) {
			FuncionesSwing.manageException(this, e,logger,UsuarioConstantesFunciones.CLASSNAME);
		}
	}
	
