	@SuppressWarnings("rawtypes")
	public static void setActualComboBoxUsuarioGenerico(Long idUsuarioSeleccionado,JComboBox jComboBoxUsuario,List<Usuario> usuariosLocal)throws Exception {
		try {
			Usuario  usuarioTemp=null;

			for(Usuario usuarioAux:usuariosLocal) {
				if(usuarioAux.getId()!=null && usuarioAux.getId().equals(idUsuarioSeleccionado)) {
					usuarioTemp=usuarioAux;
					break;
				}
			}

			jComboBoxUsuario.setSelectedItem(usuarioTemp);

		} catch(Exception e) {
			throw e;
		}
	}
	
