	public void guardarDatosBusquedaSession() throws Exception {
		
		//UsuarioSessionBean usuarioSessionBean=new UsuarioSessionBean();
		
		if(this.usuarioSessionBean==null) {
			this.usuarioSessionBean=new UsuarioSessionBean();
		}
		
		this.usuarioSessionBean.setsUltimaBusquedaUsuario(this.getsAccionBusqueda());
		this.usuarioSessionBean.setiNumeroPaginacion(this.getiNumeroPaginacion());
		this.usuarioSessionBean.setiNumeroPaginacionPagina(this.getiNumeroPaginacionPagina());
		
		if(this.getsAccionBusqueda().equals("Todos")) {
			;
		}
		

		else if(this.getsAccionBusqueda().equals("BusquedaPorNombre")) {
			usuarioSessionBean.setnombre(this.getnombreBusquedaPorNombre());	
		}
		else if(this.getsAccionBusqueda().equals("BusquedaPorUserName")) {
			usuarioSessionBean.setuser_name(this.getuser_nameBusquedaPorUserName());	
		}
		else if(this.getsAccionBusqueda().equals("FK_IdEmpresa")) {
			usuarioSessionBean.setid_empresa(this.getid_empresaFK_IdEmpresa());	
		}
		
	}
	
	public void traerDatosBusquedaDesdeSession() throws Exception {
		//UsuarioSessionBean usuarioSessionBean=new UsuarioSessionBean();
		
		if(this.usuarioSessionBean==null) {
			this.usuarioSessionBean=new UsuarioSessionBean();
		}
		
		if(this.usuarioSessionBean.getsUltimaBusquedaUsuario()!=null&&!this.usuarioSessionBean.getsUltimaBusquedaUsuario().equals("")) {
			this.setsAccionBusqueda(usuarioSessionBean.getsUltimaBusquedaUsuario());
			this.setiNumeroPaginacion(usuarioSessionBean.getiNumeroPaginacion());
			this.setiNumeroPaginacionPagina(usuarioSessionBean.getiNumeroPaginacionPagina());		
				
			if(this.getsAccionBusqueda().equals("Todos")) {
				;
			}
			

			 else if(this.getsAccionBusqueda().equals("BusquedaPorNombre")) {
				this.setnombreBusquedaPorNombre(usuarioSessionBean.getnombre());
				usuarioSessionBean.setnombre("");
			}
			 else if(this.getsAccionBusqueda().equals("BusquedaPorUserName")) {
				this.setuser_nameBusquedaPorUserName(usuarioSessionBean.getuser_name());
				usuarioSessionBean.setuser_name("");
			}
			 else if(this.getsAccionBusqueda().equals("FK_IdEmpresa")) {
				this.setid_empresaFK_IdEmpresa(usuarioSessionBean.getid_empresa());
				usuarioSessionBean.setid_empresa(-1L);
			}
		}
		
		this.usuarioSessionBean.setsUltimaBusquedaUsuario("");
		this.usuarioSessionBean.setiNumeroPaginacion(UsuarioConstantesFunciones.INUMEROPAGINACION);
		this.usuarioSessionBean.setiNumeroPaginacionPagina(0);
		
	}
	
